import React from 'react';
import './PartnershipTab.css';

const PartnershipTab = ({ commentary }) => {
  if (!commentary || commentary.length === 0) return <p>No data available.</p>;

  const partnershipsByInning = [];
  let currentInning = -1;
  let striker = null;
  let nonStriker = null;
  let currentPartnership = null;

  commentary.forEach((ball) => {
    const inning = ball.inning;

    if (inning !== currentInning) {
      // Start new inning
      currentInning = inning;
      partnershipsByInning[currentInning] = [];
      striker = null;
      nonStriker = null;
      currentPartnership = null;
    }

    const batsman = ball.batsman?.name;
    const runs = ball.runs || 0;
    const extras = ball.extras || 0;
    const isWicket = !!ball.dismissal;

    // Start first partnership
    if (!striker) {
      striker = batsman;
      return;
    }

    if (!nonStriker && batsman !== striker) {
      nonStriker = batsman;
      currentPartnership = {
        batsmen: [striker, nonStriker],
        runs: 0,
        balls: 0,
      };
      partnershipsByInning[currentInning].push(currentPartnership);
    }

    // Only count if valid
    if (
      currentPartnership &&
      currentPartnership.batsmen.includes(batsman)
    ) {
      currentPartnership.runs += runs + extras;
      currentPartnership.balls += 1;
    }

    if (isWicket) {
      // If striker is out, swap in new batsman
      if (batsman === striker) {
        striker = null;
      } else if (batsman === nonStriker) {
        nonStriker = null;
      }

      striker = batsman === striker ? null : striker;
      nonStriker = batsman === nonStriker ? null : nonStriker;

      // Reset partnership
      currentPartnership = null;
    } else {
      // On valid delivery, rotate strike if runs are odd
      if ((runs % 2 === 1) && striker && nonStriker) {
        [striker, nonStriker] = [nonStriker, striker];
      }
    }
  });

  return (
    <div className="partnership-tab">
      {partnershipsByInning.map((partnerships, idx) => (
        <div key={idx} className="inning-block">
          <h3>Inning {idx + 1}</h3>
          <table className="partnership-table">
            <thead>
              <tr>
                <th>Batsman 1</th>
                <th>Batsman 2</th>
                <th>Runs</th>
                <th>Balls</th>
              </tr>
            </thead>
            <tbody>
              {partnerships.map((p, i) => (
                <tr key={i}>
                  <td>{p.batsmen[0]}</td>
                  <td>{p.batsmen[1]}</td>
                  <td>{p.runs}</td>
                  <td>{p.balls}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      ))}
    </div>
  );
};

export default PartnershipTab;
