import React, { useEffect, useState } from 'react';
import { getTossWinnerName, getTossDecision, matchYear } from './../../utils/matchUtils';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';
import CountdownTimer from './../CountdownTimer';

const TeamLiveMatches = ({ teamName, matches }) => {
  const { id } = useParams();
  const navigate = useNavigate();

  const getTeamRuns = (match, teamId) => {
    if (!match.runs || !Array.isArray(match.runs)) return '';
    const innings = match.runs.filter(r => r.team_id === teamId);
    return innings
      .map(r => `${r.score}/${r.wickets} (${r.overs} ov)`)
      .join(' & ');
  };

  const matchStageName = (match) => {
    return match?.stage?.name + ' ' + matchYear(match);
  };

  const venueName = (match) => {
    return match.venue?.name && match.venue?.city
      ? `${match.venue.name}, ${match.venue.city}`
      : 'TBD';
  };

  return (
    <div className="team-matches">
      <h2>Live Matches</h2>

      {matches.length === 0 ? (
        <p>No live matches available.</p>
      ) : (
        <div className="match-list">
          {matches.map((match) => (
            <div
              key={match.id}
              className="match-card"
              onClick={() => navigate(`/match/${match.id}`)}
            >
              {match.type && (
                <span className="match-type-tag">{match.type}</span>
              )}

              <h4>{matchStageName(match)}</h4>

              <p>
                <img src={match.localteam.image_path} alt="" className="flag-icon" />
                {match.localteam.name} {getTeamRuns(match, match.localteam.id) && (getTeamRuns(match, match.localteam.id))}
              </p>
              <p>
                <img src={match.visitorteam.image_path} alt="" className="flag-icon" />
                {match.visitorteam.name} {getTeamRuns(match, match.visitorteam.id) && (getTeamRuns(match, match.visitorteam.id))}
              </p>

              {match?.note ? (
                <p className="status">{match.note}</p>
              ) : (
                match?.toss_won_team_id && match?.elected && (
                  <p>{getTossWinnerName(match)} won the toss and elected to {getTossDecision(match)}</p>
                )
              )}

              <div className="match-meta">
                <span className="venue">Venue: {venueName(match)}</span>
              </div>

              <div className="notify-btn">
                {match.id && (
                  <button
                    onClick={(e) => {
                      e.stopPropagation();
                      navigate(`/match/${match.id}`);
                    }}
                  >
                    Scorecard
                  </button>
                )}

                {match?.id && match?.stage_id && match.season_id && (
                  <button
                    onClick={(e) => {
                      e.stopPropagation();
                      navigate(`/series/${match.stage_id}/${match.season_id}`, {
                        state: { seriesLabel: matchStageName(match), seriesCode: match?.type },
                      });
                    }}
                  >
                    Series
                  </button>
                )}
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}

export default TeamLiveMatches;
