import React, { useEffect, useState } from 'react';
import axios from 'axios';

export default function PartnershipTab({ matchId }) {
  const [data, setData] = useState(null);

  useEffect(() => {
    axios.get(`/api/match/${matchId}`).then(res => {
      setData(res.data.data);
    });
  }, [matchId]);

  if (!data) return <div>Loading...</div>;

  const lineup = data.lineup || [];
  const balls = data.balls || [];

  const getPlayerName = (id) => {
    const player = lineup.find(p => p.id === id);
    return player?.fullname || 'Unknown';
  };

  const groupedByInning = {};
  balls.forEach(ball => {
    const inning = ball.scoreboard || 'Inning';
    if (!groupedByInning[inning]) groupedByInning[inning] = [];
    groupedByInning[inning].push(ball);
  });

  const partnershipsPerInning = {};

  Object.entries(groupedByInning).forEach(([inning, deliveries]) => {
    const partnerships = [];
    let striker = null;
    let nonStriker = null;
    let runs = 0;
    let ballsCount = 0;

    deliveries.forEach(ball => {
      const currentStriker = ball.batsman_id;

      if (striker === null) {
        striker = currentStriker;
      } else if (nonStriker === null && currentStriker !== striker) {
        nonStriker = currentStriker;
      }

      runs += ball.score?.runs || 0;
      ballsCount++;

      if (ball.wicket) {
        partnerships.push({
          striker,
          nonStriker,
          runs,
          balls: ballsCount,
        });

        // Reset for next partnership
        // New striker is the new batsman in next delivery
        striker = null;
        nonStriker = null;
        runs = 0;
        ballsCount = 0;
      }
    });

    // Push final partnership if incomplete (last pair not out)
    if (ballsCount > 0) {
      partnerships.push({
        striker,
        nonStriker,
        runs,
        balls: ballsCount,
      });
    }

    partnershipsPerInning[inning] = partnerships;
  });

  return (
    <div>
      <h3>Batting Partnerships</h3>
      {Object.entries(partnershipsPerInning).map(([inning, partnerships]) => (
        <div key={inning}>
          <h4>{inning}</h4>
          {partnerships.map((p, idx) => {
            const names = [p.striker, p.nonStriker].filter(Boolean).map(getPlayerName).join(' & ');
            return (
              <div key={idx} style={{ marginBottom: '10px' }}>
                <strong>{names}</strong>: {p.runs} runs in {p.balls} balls
              </div>
            );
          })}
        </div>
      ))}
    </div>
  );
}
