// src/components/MatchDetails.js
import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';

import InfoTab from './tabs/InfoTab';
import ScorecardTab from './tabs/ScorecardTab';
import CommentaryTab from './tabs/CommentaryTab';
import LineupsTab from './tabs/LineupsTab';

const MatchDetails = () => {
  const { id } = useParams();
  const [match, setMatch] = useState(null);
  const [loading, setLoading] = useState(true);
  const [activeTab, setActiveTab] = useState('info');

  useEffect(() => {
    const fetchMatchDetails = async () => {
      try {
        const res = await axios.get(`/api/match/${id}`);
        setMatch(res.data.data);
      } catch (err) {
        console.error('Error fetching match details', err);
      } finally {
        setLoading(false);
      }
    };
    fetchMatchDetails();
  }, [id]);

  if (loading) return <div>Loading match details...</div>;
  if (!match) return <div>Error loading match data</div>;

  const renderTab = () => {
    switch (activeTab) {
      case 'info':
        return <InfoTab match={match} />;
      case 'scorecard':
        return <ScorecardTab
                 scoreboards={match.scoreboards}
                 batting={match.batting}
                 bowling={match.bowling}
                 localteam={match.localteam}
                 visitorteam={match.visitorteam}
               />;
      case 'commentary':
        return <CommentaryTab matchId={match.id} />;
      case 'lineups':
        return <LineupsTab match={match} />;
      default:
        return null;
    }
  };

  return (
    <div className="match-details">
      <h2>{match.localteam?.name} vs {match.visitorteam?.name}</h2>
      <div className="tabs">
        <button onClick={() => setActiveTab('info')} className={activeTab === 'info' ? 'active' : ''}>Info</button>
        <button onClick={() => setActiveTab('scorecard')} className={activeTab === 'scorecard' ? 'active' : ''}>Scorecard</button>
        <button onClick={() => setActiveTab('commentary')} className={activeTab === 'commentary' ? 'active' : ''}>Commentary</button>
        <button onClick={() => setActiveTab('lineups')} className={activeTab === 'lineups' ? 'active' : ''}>Lineups</button>
      </div>
      <div className="tab-content">
        {renderTab()}
      </div>
    </div>
  );
};

export default MatchDetails;
