import React from 'react';
import './PartnershipTab.css';

const PartnershipTab = ({ commentary }) => {
  if (!commentary || !Array.isArray(commentary)) return <p>No commentary data available.</p>;

  const inningsGroups = {};
  commentary.forEach(entry => {
    const inning = entry.inning;
    if (!inningsGroups[inning]) inningsGroups[inning] = [];
    inningsGroups[inning].push(entry);
  });

  const getDismissedBatsman = (dismissalText) => {
    if (!dismissalText) return null;
    const parts = dismissalText.toLowerCase().split(' ');
    const idx = parts.indexOf('b');
    if (idx > 0) return parts[idx - 1];
    return null;
  };

  const partnershipsByInnings = Object.entries(inningsGroups).map(([inningNum, balls]) => {
    let striker = null;
    let nonStriker = null;
    let runs = 0;
    let deliveries = 0;
    const partnerships = [];

    balls.forEach(ball => {
      const batsman = ball.batsman?.name;
      const extras = ball.extras || 0;
      const runsThisBall = ball.runs || 0;

      if (!striker) {
        striker = batsman;
      } else if (!nonStriker && batsman !== striker) {
        nonStriker = batsman;
      }

      runs += runsThisBall + extras;
      deliveries++;

      if (ball.dismissal) {
        partnerships.push({
          batsmen: [striker, nonStriker],
          runs,
          deliveries
        });

        const outName = getDismissedBatsman(ball.dismissal);
        if (outName?.toLowerCase() === striker?.toLowerCase()) {
          striker = null;
        } else if (outName?.toLowerCase() === nonStriker?.toLowerCase()) {
          nonStriker = null;
        } else {
          striker = null;
          nonStriker = null;
        }

        runs = 0;
        deliveries = 0;
      }
    });

    if (striker && nonStriker && (runs > 0 || deliveries > 0)) {
      partnerships.push({
        batsmen: [striker, nonStriker],
        runs,
        deliveries
      });
    }

    return {
      inning: parseInt(inningNum) + 1,
      partnerships,
    };
  });

  return (
    <div className="partnership-tab">
      {partnershipsByInnings
        .sort((a, b) => b.inning - a.inning)
        .map(({ inning, partnerships }) => (
          <div key={inning} className="innings-block">
            <h3>Innings {inning}</h3>
            {partnerships.map((p, idx) => (
              <div key={idx} className="partnership-row">
                <strong>{p.batsmen[0]} & {p.batsmen[1]}</strong> â€“ {p.runs} runs in {p.deliveries} balls
              </div>
            ))}
          </div>
        ))}
    </div>
  );
};

export default PartnershipTab;
