import React, { useEffect, useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import './Home.css';

const Home = () => {
  const [matches, setMatches] = useState([]);
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchMatches = async () => {
      try {
        const res = await fetch('/api/live');
        const json = await res.json();

        const liveMatches = (json?.data || []).filter(
          m => m.matchStarted && !m.matchEnded && m.status !== 'Match not started'
        );

        setMatches(liveMatches.slice(0, 4));
      } catch (err) {
        console.error('Error fetching live matches:', err);
      } finally {
        setLoading(false);
      }
    };

    fetchMatches();
  }, []);

  if (loading) return <p>Loading live matches...</p>;
  if (!matches.length) return <p>No live matches found.</p>;

  return (
    <div className="home-container">
      <h2>Live Matches</h2>
      <div className="match-cards-row">
        {matches.map(match => (
          <div className="match-card">
            <div className="series-line">
              <span>{match.seriesName}</span>
            </div>

            <div className="match-meta">
              <span>{match.name}</span>
            </div>

            <div className="match-meta">
              <span className="venue">{match.venue}</span>
            </div>

            {match.teamInfo?.map((team, idx) => {
               const innings = match.score?.filter(s => s.inning?.toLowerCase().includes(team.name.toLowerCase())) || [];
               return (
                 <div className="team-score-row" key={team.name}>
                   <div className="team-name">
                     <img src={team.img} alt={team.name} />
                     <span>{team.shortname}</span>
                   </div>
                   <div className="score-text">
                     {innings.map((inn, i) => (
                       <span key={i}>
                         {inn.r}/{inn.w} ({inn.o})
                         {i < innings.length - 1 && ' & '}
                       </span>
                     ))}
                   </div>
                 </div>
               );
             })}

             <p className="status">{match.status}</p>

             {match.series_id && (
               <Link
                 to={`/series/${match.series_id}`}
                 className="series-link"
                 state={{ fromHome: true }}
                 onClick={(e) => e.stopPropagation()}
               >
                 {match.seriesName || 'Series'}
               </Link>
             )}
           </div>
      ))}
    </div>
  </div>
);
};

export default Home;
