import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import './css/Players.css';

export default function Players() {
  const navigate = useNavigate();
  const [players, setPlayers] = useState([]);
  const [query, setQuery] = useState('');
  const [offset, setOffset] = useState(0);
  const [loading, setLoading] = useState(true);

  const load = async () => {
    setLoading(true);

    const url = query
      ? `/api/players/search?q=${encodeURIComponent(query)}`
      : `/api/players?offset=${offset}`;

    try {
      const res = await fetch(url);
      const data = await res.json();
      const basicPlayers = data.data || [];

      // Now fetch player images in parallel
      const enrichedPlayers = await Promise.all(
        basicPlayers.map(async (p) => {
          try {
            const imgRes = await fetch(`/api/player/${p.id}`);
            const imgData = await imgRes.json();
            const image = imgData?.data?.playerImg || '';
            return { ...p, image };
          } catch (err) {
            return { ...p, image: '' };
          }
        })
      );

      setPlayers(enrichedPlayers);
    } catch (err) {
      console.error('Error fetching players:', err);
      setPlayers([]);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => { load(); }, [query, offset]);

  return (
    <div className="players-container">
      <h2>Players</h2>
      <input
        type="text"
        placeholder="Search players..."
        value={query}
        onChange={e => setQuery(e.target.value)}
      />

      {loading ? (
        <p>Loading...</p>
      ) : (
        <div className="player-grid">
          {players.map((p) => (
            <Link key={p.id} to={`/player/${p.id}`} className="player-card">
              <img
                src={p.image || 'https://h.cricapi.com/img/icon512.png'}
                alt={p.name}
                width="80"
                height="80"
                onError={(e) => {
                  e.target.src = 'https://h.cricapi.com/img/icon512.png';
                }}
              />
              <h4>{p.name}</h4>
              <p>{p.country}</p>
            </Link>
          ))}
        </div>
      )}

      {!query && (
        <div className="paging">
          <button onClick={() => setOffset(o => Math.max(o - 50, 0))} disabled={offset === 0}>Prev</button>
          <button onClick={() => setOffset(o => o + 50)}>Next</button>
        </div>
      )}
    </div>
  );
}
