import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import Breadcrumb from './Breadcrumb';
import './PlayerDetails.css';

const STAT_ORDER = ['m', 'inn', 'runs', 'bf', 'hs', 'avg', 'sr', 'no', '4s', '6s', '50', '100', '200'];
const BOWLING_STAT_ORDER = ['m', 'inn', 'b', 'runs', 'wkts', 'avg', 'econ', 'sr', 'bbi', 'bbm', '5w', '10w'];

const PlayerDetails = () => {
  const { id } = useParams();
  const [player, setPlayer] = useState(null);
  const [articles, setArticles] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchPlayer = async () => {
      const res = await fetch(`/api/player/${id}`);
      const json = await res.json();
      setPlayer(json.data);

      // ðŸ‘‡ After setting player, fetch news
      const playerName = json.data?.name?.replace(/\s+/g, '+');
      if (playerName) {
        try {
          const response = await fetch(`/api/news?q=${playerName}`);
          const news = await response.json();
          setArticles(news.articles || []);
        } catch (err) {
          console.error('Error fetching news articles', err);
        } finally {
          setLoading(false);
        }
      } else {
        setLoading(false);
      }
    };

    fetchPlayer();
  }, [id]);

  if (!player) return <div>Loading player details...</div>;

  const { name, country, dateOfBirth, role, battingStyle, bowlingStyle, placeOfBirth, playerImg, stats = [] } = player;

  const breadcrumbItems = [
    { label: 'Home', link: '/' },
    { label: 'Players', link: '/players' },
    { label: name, link: `/players/${id}` },
  ];

  const getStats = (type, matchType) => {
    const filtered = stats
      .filter((s) => s.fn === type && s.matchtype === matchType)
      .reduce((acc, s) => {
        acc[s.stat.trim()] = s.value.trim();
        return acc;
      }, {});
    return filtered;
  };

  const renderTable = (matchTypes, type, order) => {
    return (
      <table>
        <thead>
          <tr>
            <th> </th>
            {order.map((stat) => (
              <th key={stat}>{stat.toUpperCase()}</th>
            ))}
          </tr>
        </thead>
        <tbody>
          {matchTypes.map((matchType) => {
            const row = getStats(type, matchType);
            return (
              <tr key={matchType}>
                <td>{matchType.toUpperCase()}</td>
                {order.map((s) => (
                  <td key={s}>{row[s] || '-'}</td>
                ))}
              </tr>
            );
          })}
        </tbody>
      </table>
    );
  };

  return (
    <div className="player-details">
      <div className="player-header">
        <div>
          <h1>{name}</h1>
          <h3>{country}</h3>
        </div>
      </div>

      <div className="personal-info">
        <h4>Personal Information</h4>
        <ul className="player-info-list">
          {dateOfBirth && (
            <li><strong>Born:</strong> {new Date(dateOfBirth).toDateString()}</li>
          )}
          {placeOfBirth && (
            <li><strong>Birth Place:</strong> {placeOfBirth}</li>
          )}
          {role && (
            <li><strong>Role:</strong> {role}</li>
          )}
          {battingStyle && (
            <li><strong>Batting Style:</strong> {battingStyle}</li>
          )}
          {bowlingStyle && (
            <li><strong>Bowling Style:</strong> {bowlingStyle}</li>
          )}
        </ul>
      </div>

      <div className="career-section">
        <h3>Batting Career Summary</h3>
        {renderTable(['test', 'odi', 't20', 'ipl'], 'batting', STAT_ORDER)}

        <h3>Bowling Career Summary</h3>
        {renderTable(['test', 'odi', 't20', 'ipl'], 'bowling', BOWLING_STAT_ORDER)}
      </div>


      {loading ? (
        <p>Loading articles...</p>
      ) : articles.length === 0 ? (
        <></>
      ) : (
        <>
          <h3>News</h3>
          <div className="news-container">
            <div className="news-grid">
              {articles.map((article, index) => (
                <div key={index} className="news-card">
                  <img
                    src={article.image}
                    alt={article.title}
                    className="news-image"
                  />
                  <h2 className="news-heading">{article.title}</h2>
                  <p className="news-meta">
                    {article.source.name} â€¢{' '}
                    {new Date(article.publishedAt).toLocaleString()}
                  </p>
                  <p className="news-description">{article.description}</p>
                  <a
                    href={article.url}
                    target="_blank"
                    rel="noreferrer"
                    className="news-link"
                  >
                    Read more â†’
                  </a>
                </div>
              ))}
            </div>
          </div>
        </>
      )}
    </div>
  );
};

export default PlayerDetails;
