import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import './Players.css';

export default function Players() {
  const [players, setPlayers] = useState([]);
  const [query, setQuery] = useState('');
  const [offset, setOffset] = useState(0);

  const load = async () => {
    const url = query
      ? `/api/players/search?q=${encodeURIComponent(query)}`
      : `/api/players?offset=${offset}`;
    const res = await fetch(url);
    const data = await res.json();
    setPlayers(data.data || []);
  };

  useEffect(() => { load(); }, [query, offset]);

  return (
    <div className="players-container">
      <h2>Players</h2>
      <input
        type="text"
        placeholder="Search players..."
        value={query}
        onChange={e => setQuery(e.target.value)}
      />
<div className="player-grid">
  {players.map((p) => (
    <div className="player-card" key={p.id} onClick={() => navigate(`/players/${p.id}`)}>
      <h4>{p.name}</h4>
      <p>{p.country}</p>
    </div>
  ))}
</div>
      {!query && (
        <div className="paging">
          <button onClick={() => setOffset(o => Math.max(o - 50, 0))} disabled={offset === 0}>Prev</button>
          <button onClick={() => setOffset(o => o + 50)}>Next</button>
        </div>
      )}
    </div>
  );
}
