import React, { useEffect, useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import CountdownTimer from './../CountdownTimer';
import './css/FeaturedMatches.css';

const FeaturedMatches = () => {
  const [matches, setMatches] = useState([]);
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchMatches = async () => {
      try {
        const res = await fetch('/api/schedule');
        const json = await res.json();
        const data = json?.data || [];

        const liveMatchStatuses = [
          '1st Innings', '2nd Innings', '3rd Innings', '4th Innings',
          'Stump Day 1', 'Stump Day 2', 'Stump Day 3', 'Stump Day 4',
          'Innings Break', 'Tea Break', 'Lunch', 'Dinner', 'Int.', 'Delayed',
        ];

        const completedMatchStatuses = [
          'Finished', 'Cancl', 'Aban.',
        ];

        const upcomingMatchStatuses = [
          'NS', 'Postp.',
        ];

        const liveMatches = data.filter(m => liveMatchStatuses.includes(m.status));
        const upcomingMatches = data.filter(m => upcomingMatchStatuses.includes(m.status));
        const completedMatches = data.filter(m => completedMatchStatuses.includes(m.status));

        const featured = [];

        // Add up to 2 live matches first
        featured.push(...liveMatches.slice(0, 2));

        // Track how many more we need to make 3
        let remaining = 3 - featured.length;

        // Add 1 upcoming match if available and needed
        if (remaining > 0 && upcomingMatches.length > 0) {
          featured.push(upcomingMatches[0]);
          remaining--;
        }

        // Add completed match if still needed
        if (remaining > 0 && completedMatches.length > 0) {
          featured.push(completedMatches[0]);
          remaining--;
        }

        // Gather remaining matches in extras
        const extras = [
          ...liveMatches.slice(2),
          ...upcomingMatches.slice(1),
          ...completedMatches.slice(1),
        ];

        // If still not 3 featured matches (in extreme edge cases), fill from extras
        while (featured.length < 3 && extras.length > 0) {
          featured.push(extras.shift());
        }

        setMatches(featured);
      } catch (err) {
        console.error('Error fetching featured matches:', err);
      } finally {
        setLoading(false);
      }
    };

    fetchMatches();
  }, []);

  const formattedDate = (input) => {
    if (!input) return 'N/A';
    try {
      return new Date(input).toLocaleString();
    } catch (e) {
      return 'Invalid date';
    }
  };

  const getTeamRuns = (match, teamId) => {
    if (!match.runs || !Array.isArray(match.runs)) return '';
    const innings = match.runs.filter(r => r.team_id === teamId);
    return innings
      .map(r => `${r.score}/${r.wickets} (${r.overs} ov)`)
      .join(' & ');
  };

  if (loading) return <p>Loading featured matches...</p>;
  if (!matches.length) return <p>No matches found.</p>;

  return (
    <>
      <div className="live-matches-header">
        <h3>🏏 Top Matches</h3>
        <a href="/schedule">View All</a>
      </div>

      <div className="match-cards-row">
        {matches.map(match => (
          <div key={match.id} className="match-card" onClick={() => navigate(`/match/${match.id}`, { state: { from: 'Home' } })}>
            <div className="series-line">
              <span>{match.league?.name}, {match.round}</span>
            </div>

            {match.status === 'NS' && (
              <div className="match-meta">
                <span>{match.localteam?.name} vs {match.visitorteam?.name}</span>
              </div>
            )}

            <div className="match-meta">
              <span className="venue">{match.venue?.name}, {match.venue?.city}</span>
            </div>

            {getTeamRuns(match, match.localteam.id) && (
              <p>{match.localteam.name}: {getTeamRuns(match, match.localteam.id)}</p>
            )}
            {getTeamRuns(match, match.visitorteam.id) && (
              <p>{match.visitorteam.name}: {getTeamRuns(match, match.visitorteam.id)}</p>
            )}

            {match.status === 'NS' && (
              <CountdownTimer startTime={match.starting_at} />
            )}

            <p className="status">{match.note}</p>

            {match.status === 'Finished' && match.manofmatch?.fullname && (
              <p className="status">Man of the match: {match.manofmatch.fullname}</p>
            )}

          </div>
        ))}
      </div>
    </>
  );
};

export default FeaturedMatches;
