import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
import { format } from 'date-fns';
import './css/Series.css';

export default function Series() {
  const [seriesList, setSeriesList] = useState([]);
  const [search, setSearch] = useState('');
  const [fallbackImg, setFallbackImg] = useState(null);

  useEffect(() => {
    axios.get('/api/series').then(res => {
      setSeriesList(res.data.data || []);
    });
  }, []);

  const filtered = seriesList.filter(s =>
    s.name?.toLowerCase().includes(search.toLowerCase())
  );

  useEffect(() => {
    const fetchFallback = async () => {
      try {
        const res = await fetch('/api/default-images');
        const text = await res.text();
        const match = text.match(/series_url:\s*['"]([^'"]+)['"]/);
        if (match) setFallbackImg(match[1]);
      } catch (err) {
        console.error('Failed to fetch fallback image:', err);
      }
    };

    fetchFallback();
  }, []);

  const seriesName = (series) => {
    return series?.name + ' ' + series?.season;
  };

  const formatDate = (dateStr) => {
    if (!dateStr) return '';
    const d = new Date(dateStr);
    return format(d, 'dd MMM yyyy');
  };

  return (
    <div className="series-container">
      <h1>All Series</h1>
      <input
        type="text"
        placeholder="Search series..."
        value={search}
        onChange={(e) => setSearch(e.target.value)}
        className="series-search"
      />

      <div className="series-grid">
        {filtered.map(series => (
          <Link
            key={series.id}
            to={`/series/${series.id}/${series.season_id}`}
            state={{ seriesLabel: seriesName(series), seriesCode: series.code }}
            className="series-card"
          >

<div className="series-info">
  {series?.code && (
    <span className="series-type-tag">{series?.code}</span>
  )}
  <h4>{series.name} {series.season}</h4>
  {(series.startDate && series.endDate) && (
    <p className="series-dates">
      {formatDate(series.startDate)} - {formatDate(series.endDate)}
    </p>
  )}
</div>


          </Link>
        ))}
      </div>
    </div>
  );
}
