import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import CountdownTimer from './../CountdownTimer';
import NotificationButton from './../NotificationButton';
import './css/SeriesMatchesTab.css';

export default function SeriesMatchesTab({ seriesId }) {
  const [matches, setMatches] = useState([]);
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchMatches = async () => {
      try {
        const res = await fetch(`/api/series/${seriesId}/matches`);
        const json = await res.json();
        setMatches(json.data.data || []);
      } catch (err) {
        console.error('Error fetching series matches:', err);
      } finally {
        setLoading(false);
      }
    };

    fetchMatches();
  }, []);

  const liveMatchStatuses = [
    '1st Innings', '2nd Innings', '3rd Innings', '4th Innings',
    'Stump Day 1', 'Stump Day 2', 'Stump Day 3', 'Stump Day 4',
    'Innings Break', 'Tea Break', 'Lunch', 'Dinner', 'Int.',
  ];

  const otherMatchStatuses = [
    'Delayed',
  ];

  const completedMatchStatuses = [
    'Finished', 'Cancl', 'Aban.',
  ];

  const upcomingMatchStatuses = [
    'NS', 'Postp.',
  ];

  const liveStatuses = [...liveMatchStatuses, ...otherMatchStatuses];
  const liveMatches = matches.filter(m => liveStatuses.includes(m.status));
  const upcomingMatches = matches.filter(m => upcomingMatchStatuses.includes(m.status));
  const completedMatches = matches.filter(m => completedMatchStatuses.includes(m.status));

  const getTossWinnerName = (match) => {
    if (match.toss_won_team_id === match.localteam.id) {
      return match.localteam.name;
    } else if (match.toss_won_team_id === match.visitorteam.id) {
      return match.visitorteam.name;
    } else {
      return '';
    }
  };

  const getTossDecision = (match) => {
    if (match.elected === 'batting') {
      return 'bat first';
    } else if (match.elected === 'bowling') {
      return 'bowl first';
    } else {
      return '';
    }
  };

  const getTeamRuns = (match, teamId) => {
    if (!match.runs || !Array.isArray(match.runs)) return '';
    const innings = match.runs.filter(r => r.team_id === teamId);
    return innings
      .map(r => `${r.score}/${r.wickets} (${r.overs} ov)`)
      .join(' & ');
  };

  return (
    <div className="series-matches-tab">
      {loading ? (
        <p>Loading matches...</p>
      ) : matches.map(match => (
        <div className="match-cards-row">

        </div>
      ))}
    </div>
  );
}
