import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import './LeagueFixtures.css';

export default function LeagueFixtures() {
  const { id } = useParams();
  const [fixtures, setFixtures] = useState([]);
  const [loading, setLoading] = useState(true);
  const [showPast, setShowPast] = useState(false);

useEffect(() => {
  const fetchFixtures = async () => {
    try {
      const today = new Date();
      const start = showPast
        ? new Date(today.getTime() - 90 * 24 * 60 * 60 * 1000).toISOString().split('T')[0]
        : today.toISOString().split('T')[0];

      const end = showPast
        ? today.toISOString().split('T')[0]
        : new Date(today.getTime() + 90 * 24 * 60 * 60 * 1000).toISOString().split('T')[0];

      const res = await fetch(
        `/api/fixtures?league_id=${id}&start=${start}&end=${end}`
      );
      const data = await res.json();
      setFixtures(data.data || []);
    } catch (err) {
      console.error('❌ Error loading fixtures:', err);
    } finally {
      setLoading(false);
    }
  };

  fetchFixtures();
}, [id, showPast]);

  return (
    <div className="fixtures-container">
      <h2>Upcoming Matches</h2>
      {loading ? (
        <p>Loading fixtures...</p>
      ) : fixtures.length === 0 ? (
        <p>No upcoming matches found.</p>
      ) : (
        <ul className="fixture-list">
          {fixtures.map(fix => (
            <li key={fix.id} className="fixture-card">
              <div className="fixture-league">{fix.league?.name}</div>
              <div className="teams">
                <img src={fix.localteam.image_path} alt="" />
                <span>{fix.localteam.name} vs {fix.visitorteam.name}</span>
                <img src={fix.visitorteam.image_path} alt="" />
              </div>
              <div className="fixture-date">{new Date(fix.starting_at).toLocaleString()}</div>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}
