import React, { useState } from 'react';
import { useParams, useLocation } from 'react-router-dom';
import SeriesMatchesTab from './series/SeriesMatchesTab';
import SeriesStandingsTab from './series/SeriesStandingsTab';
import SeriesSquadsTab from './series/SeriesSquadsTab';
import SeriesNewsTab from './series/SeriesNewsTab';
import { SeriesMatchListProvider } from './SeriesMatchListContext'; // ✅

export default function SeriesDetail() {
  const { seriesId, seasonId } = useParams();
  const [activeTab, setActiveTab] = useState('matches');
  const location = useLocation();
  const { seriesLabel, seriesCode } = location.state || {};

  const tabs = {
    matches: <SeriesMatchesTab seriesId={seriesId} seasonId={seasonId} seriesLabel={seriesLabel} seriesCode={seriesCode} />,
    standings: <SeriesStandingsTab seriesId={seriesId} seasonId={seasonId} seriesLabel={seriesLabel} seriesCode={seriesCode} />,
    squads: <SeriesSquadsTab seriesId={seriesId} seasonId={seasonId} seriesLabel={seriesLabel} seriesCode={seriesCode} />,
    news: <SeriesNewsTab seriesId={seriesId} seasonId={seasonId} seriesLabel={seriesLabel} seriesCode={seriesCode} />,
  };

  return (
    <div>
      <h1>{seriesLabel}</h1>

      <div style={{ marginBottom: '20px' }}>
        {Object.keys(tabs).map((tab) => (
          <button
            key={tab}
            onClick={() => setActiveTab(tab)}
            style={{
              marginRight: '10px',
              fontWeight: activeTab === tab ? 'bold' : 'normal',
              padding: '8px 12px'
            }}
          >
            {tab.charAt(0).toUpperCase() + tab.slice(1)}
          </button>
        ))}
      </div>

      {/* ✅ Wrap only the tab content with SeriesMatchListProvider */}
      <SeriesMatchListProvider seriesId={seriesId}>
        {tabs[activeTab]}
      </SeriesMatchListProvider>
    </div>
  );
}
