import React from 'react';
import './CommentaryTab.css';

const CommentaryTab = ({ commentary = [], scorecard = [] }) => {
  if (!commentary.length) return <p>No commentary available.</p>;

  // Group by innings
  const groupedByInning = commentary.reduce((acc, ball) => {
    const inning = ball.inning ?? 0;
    if (!acc[inning]) acc[inning] = [];
    acc[inning].push(ball);
    return acc;
  }, {});

  const capitalize = str =>
    str
      .split(' ')
      .map(w => (w.length > 1 ? w[0].toUpperCase() + w.slice(1).toLowerCase() : w))
      .join(' ');

  const getEmojiForDismissal = (dismissal) => {
    if (!dismissal) return '';
    if (dismissal.includes('b ')) return '🎯';
    if (dismissal.includes('lbw')) return '✋';
    if (dismissal.includes('st ')) return '🧤';
    if (dismissal.includes('run out')) return '🏃‍♂️';
    if (dismissal.includes('retired')) return '🚶‍♂️';
    return '☠️';
  };

  // Get score summary from scorecard
  const getInningScoreInfo = (inningIndex) => {
    const inning = scorecard?.[inningIndex];
    if (!inning) return '';
    const totalRuns = inning.extras?.r + inning.extras?.b;
    const batterStr = inning.batting?.map(b => `${b.batsman?.name || '–'} (${b.r || 0} off ${b.b || 0})`).join(', ');
    const lastBowler = inning.bowling?.[inning.bowling.length - 1];
    const bowlerStr = lastBowler ? `${lastBowler.bowler?.name} ${lastBowler.o || 0}-${lastBowler.r || 0}-${lastBowler.w || 0}` : '';
    return `Batting: ${batterStr} | Bowler: ${bowlerStr} | Extras: ${totalRuns}`;
  };

  // Order innings descending (latest inning first)
  const sortedInnings = Object.keys(groupedByInning).sort((a, b) => b - a);

  return (
    <div className="commentary-tab">
      {sortedInnings.map((inningIndex) => {
        const balls = groupedByInning[inningIndex];
        const overMap = {};

        balls.forEach(ball => {
          const overKey = ball.over;
          if (!overMap[overKey]) overMap[overKey] = [];
          overMap[overKey].push(ball);
        });

        const sortedOvers = Object.keys(overMap).sort((a, b) => b - a); // latest over on top

        return (
          <div key={inningIndex} className="inning-section">
            <h3>Inning {parseInt(inningIndex) + 1}</h3>
            {sortedOvers.map((over, overIdx) => {
              const deliveries = overMap[over].sort((a, b) => b.ball - a.ball); // show 6 to 1
              const overRuns = deliveries.reduce((sum, b) => sum + (b.runs || 0) + (b.extras || 0), 0);

              return (
                <div key={overIdx} className="over-block">
                  <div className="over-header">
                    <span className="over-number">Over {over}</span>
                    <span className="over-circle">{overRuns}</span>
                    <div className="over-progress">
                      {deliveries.map((b, idx) => (
                        <span
                          key={idx}
                          className={`ball ${b.runs === 0 && !b.extras ? 'dot' : b.runs >= 4 ? 'boundary' : 'run'}`}
                        >
                          {b.runs || (b.extras ? 'E' : '•')}
                        </span>
                      ))}
                    </div>
                  </div>

                  {deliveries.map((entry, idx) => {
                    const batsman = entry.batsman?.name || 'Unknown';
                    const bowler = entry.bowler?.name || 'Unknown';
                    const overBall = `${entry.over}.${entry.ball}`;
                    const dismissal = entry.dismissal;
                    const emoji = getEmojiForDismissal(dismissal);
                    const dismissalText = dismissal ? `${emoji} ${capitalize(dismissal)} – OUT` : null;

                    return (
                      <div key={idx} className="commentary-entry">
                        <div className="ball-info">
                          <strong>Ball {overBall}</strong> – {bowler} to {batsman}
                        </div>
                        <div className="commentary-text">
                          {dismissalText ? (
                            <span className="dismissal">{dismissalText}</span>
                          ) : (
                            <>
                              <span className="runs">Runs: {entry.runs}</span>
                              {entry.extras ? (
                                <span className="extras"> + Extras: {entry.extras}</span>
                              ) : null}
                            </>
                          )}
                        </div>
                      </div>
                    );
                  })}

                  <div className="over-summary">
                    <strong>Over Summary:</strong> {getInningScoreInfo(inningIndex)}
                  </div>
                </div>
              );
            })}
          </div>
        );
      })}
    </div>
  );
};

export default CommentaryTab;
