// src/pages/Home.js
import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import './Home.css';

const Home = () => {
  const [matches, setMatches] = useState([]);

  useEffect(() => {
    const fetchLiveMatches = async () => {
      try {
        const res = await fetch('/api/currentMatches');
        const json = await res.json();
        const filtered = (json.data || []).filter(
          m => m.matchStarted && !m.matchEnded && m.status !== 'Match not started'
        );
        setMatches(filtered.slice(0, 4));
      } catch (err) {
        console.error('Failed to load live matches', err);
      }
    };

    fetchLiveMatches();
  }, []);

  return (
    <div className="home-container">
      <h2>Live Matches</h2>
      <div className="live-matches-row">
        {matches.map(match => (
          <div className="match-card" key={match.id}>
            <div className="teams">
              <div className="team">
                <img src={match.teamInfo?.[0]?.img} alt={match.teamInfo?.[0]?.name} />
                <span>{match.teamInfo?.[0]?.shortname}</span>
              </div>
              <span className="vs">vs</span>
              <div className="team">
                <img src={match.teamInfo?.[1]?.img} alt={match.teamInfo?.[1]?.name} />
                <span>{match.teamInfo?.[1]?.shortname}</span>
              </div>
            </div>

            {match.score?.length > 0 && (
              <div className="score">
                {match.score.map((s, idx) => (
                  <div key={idx}>
                    <strong>{s.inning}</strong>: {s.r}/{s.w} ({s.o} ov)
                  </div>
                ))}
              </div>
            )}

            <p className="status">{match.status}</p>

            {match.seriesId && match.seriesName && (
              <Link to={`/series/${match.seriesId}`} className="series-link">
                {match.seriesName}
              </Link>
            )}

            <Link to={`/match/${match.id}`} className="details-link">
              View Details â†’
            </Link>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Home;
