import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import './Home.css';

const Home = () => {
  const [liveMatches, setLiveMatches] = useState([]);

  useEffect(() => {
    const fetchLiveMatches = async () => {
      try {
        const res = await fetch('/api/live');
        const json = await res.json();
        const filtered = (json.data || []).filter(
          (match) =>
            match.matchStarted === true &&
            match.matchEnded === false &&
            match.status !== 'Match not started'
        );
        setLiveMatches(filtered.slice(0, 4)); // Show top 3–4
      } catch (err) {
        console.error('Failed to fetch live matches', err);
      }
    };

    fetchLiveMatches();
  }, []);

  const renderScore = (score) => {
    if (!score || score.length === 0) return null;
    return score.map((s, i) => (
      <div key={i} className="match-score">
        <strong>{s.inning}:</strong> {s.r}/{s.w} ({s.o} ov)
      </div>
    ));
  };

  return (
    <div className="home-container">
      <h2>Live Matches</h2>
      <div className="live-match-grid">
        {liveMatches.map((match) => (
          <Link key={match.id} to={`/match/${match.id}`} className="match-card">
            <div className="teams">
              <span>{match.teams?.[0]}</span> vs <span>{match.teams?.[1]}</span>
            </div>
            <div className="status">{match.status}</div>
            {renderScore(match.score)}
          </Link>
        ))}
      </div>
    </div>
  );
};

export default Home;
