import React, { useEffect, useState } from 'react';
import { useParams, Link } from 'react-router-dom';
import './css/PlayerDetails.css';

export default function PlayerDetail() {
  const { id } = useParams(); // CricketData player ID
  const [player, setPlayer] = useState(null);
  const [loading, setLoading] = useState(true);

  const load = async () => {
    try {
      const res = await fetch(`/api/player/${id}`);
      const data = await res.json();
      setPlayer(data);
    } catch (err) {
      console.error('Failed to fetch player data:', err);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    load();
  }, [id]);

  if (loading) return <p>Loading player details...</p>;
  if (!player) return <p>Player not found.</p>;
console.log(player);
  const { name, country, role, dateOfBirth, battingStyle, bowlingStyle, placeOfBirth, image_path, stats } = player;

  const renderStatTable = (type) => {
    const filtered = stats?.filter(s => s.fn === type);
    if (!filtered?.length) return null;

    const matchTypes = [...new Set(filtered.map(s => s.matchtype))];

    return matchTypes.map(matchType => {
      const rows = filtered.filter(s => s.matchtype === matchType);
      return (
        <div key={matchType}>
          <h4>{type.toUpperCase()} – {matchType.toUpperCase()}</h4>
          <table>
            <thead>
              <tr>
                {rows.map((r, i) => <th key={i}>{r.stat}</th>)}
              </tr>
            </thead>
            <tbody>
              <tr>
                {rows.map((r, i) => <td key={i}>{r.value}</td>)}
              </tr>
            </tbody>
          </table>
        </div>
      );
    });
  };

  return (
    <div className="player-detail">
      <Link to="/player" className="back-link">← Back to Players</Link>
      <h2>{name}</h2>
      <p><strong>Country:</strong> {country}</p>
      {image_path && (
        <img className="player-image" src={image_path} alt={name} />
      )}
      <p><strong>Date of Birth:</strong> {dateOfBirth?.slice(0, 10)}</p>
      <p><strong>Role:</strong> {role}</p>
      <p><strong>Batting Style:</strong> {battingStyle}</p>
      <p><strong>Bowling Style:</strong> {bowlingStyle}</p>
      <p><strong>Place of Birth:</strong> {placeOfBirth}</p>

      <div className="player-stats">
        {renderStatTable('batting')}
        {renderStatTable('bowling')}
      </div>
    </div>
  );
}
