import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './css/Players.css';

export default function Players() {
  const [players, setPlayers] = useState([]);
  const [offset, setOffset] = useState(0);
  const [countryMap, setCountryMap] = useState({});
  const navigate = useNavigate();
  const LIMIT = 30;

  useEffect(() => {
    fetch(`/api/players?offset=${offset}`)
      .then(res => res.json())
      .then(data => setPlayers(data.data || []));
  }, [offset]);

  useEffect(() => {
    fetch('/api/countries')
      .then(res => res.json())
      .then(data => {
        const map = {};
        data.data.forEach(c => {
          map[c.id] = c.name;
        });
        setCountryMap(map);
      });
  }, []);

  return (
    <div className="players-list">
      <h2>Players</h2>
      <div className="players-grid">
        {players.map(player => (
          <div
            key={player.id}
            className="player-card"
            onClick={() => navigate(`/player/${player.id}`)}
          >
            <img src={player.image_path} alt={player.fullname} className="player-img" />
            <div className="player-name">{player.fullname}</div>
            <div className="player-country">{countryMap[player.country_id]}</div>
          </div>
        ))}
      </div>
      <div className="pagination-controls">
        <button disabled={offset === 0} onClick={() => setOffset(offset - LIMIT)}>Previous</button>
        <button onClick={() => setOffset(offset + LIMIT)}>Next</button>
      </div>
    </div>
  );
}
