import React from 'react';
import './ScorecardTab.css';

const ScorecardTab = ({ match }) => {
  const scorecard = match?.scorecard;
  if (!scorecard) {
    return <p>No scorecard data available.</p>;
  }

  const formatDismissalText = (text = '') => {
    return text.replace(/(c\s+)([a-z\s]+)(\s+b\s+)([a-z\s]+)/i, (_, c, catcher, b, bowler) => {
      const capitalize = str =>
        str
          .split(' ')
          .map(word => word.charAt(0).toUpperCase() + word.slice(1))
          .join(' ');

      return `${c}${capitalize(catcher.trim())}${b}${capitalize(bowler.trim())}`;
    });
  };

  return (
    <div className="scorecard-tab">
      <h3>📊 Innings Summary</h3>

      {scorecard.map((inning, index) => (
        <div key={index} className="innings-block">
          <h4>{inning.inning}</h4>

          <h5>Batting</h5>
          <table className="scorecard-table">
            <thead>
              <tr>
                <th>Batsman</th>
                <th>R</th>
                <th>B</th>
                <th>4s</th>
                <th>6s</th>
                <th>SR</th>
                <th>Dismissal</th>
              </tr>
            </thead>
            <tbody>
              {inning.batting.map((batter, i) => (
                <tr key={i}>
                  <td>{batter.batsman?.name}</td>
                  <td>{batter.r}</td>
                  <td>{batter.b}</td>
                  <td>{batter['4s']}</td>
                  <td>{batter['6s']}</td>
                  <td>{batter.sr}</td>
                  <td>{formatDismissalText(batter['dismissal-text']) || 'Not out'}</td>
                </tr>
              ))}
            </tbody>
          </table>

          <h5>Bowling</h5>
          <table className="scorecard-table">
            <thead>
              <tr>
                <th>Bowler</th>
                <th>O</th>
                <th>M</th>
                <th>R</th>
                <th>W</th>
                <th>ECO</th>
              </tr>
            </thead>
            <tbody>
              {inning.bowling.map((bowler, i) => (
                <tr key={i}>
                  <td>{bowler.bowler?.name}</td>
                  <td>{bowler.o}</td>
                  <td>{bowler.m}</td>
                  <td>{bowler.r}</td>
                  <td>{bowler.w}</td>
                  <td>{bowler.eco}</td>
                </tr>
              ))}
            </tbody>
          </table>

          <p key={index}>
            <strong>Extras:</strong>{' '}
            {(inning.extras?.r || 0) + (inning.extras?.b || 0)}{' '}
            (Runs: {inning.extras?.r || 0}, Byes: {inning.extras?.b || 0})
          </p>
        </div>
      ))}
    </div>
  );
};

export default ScorecardTab;
