import React from 'react';
import './CommentaryTab.css';

const emojiForEvent = (entry) => {
  if (entry.dismissal) return '‚úã';
  if (entry.extras > 0) return '‚ûï';
  if (entry.runs > 0) return 'üèè';
  return '‚Ä¢';
};

const getInningLabel = (index) => {
  const labels = ['1st', '2nd', '3rd', '4th'];
  return `${labels[index] || `${index + 1}th`} Innings`;
};

const formatDismissal = (dismissal) => {
  if (!dismissal) return '';
  const styled = dismissal
    .split(' ')
    .map(word =>
      word.length > 1
        ? word.charAt(0).toUpperCase() + word.slice(1).toLowerCase()
        : word.toLowerCase()
    )
    .join(' ');

  if (styled.includes('Run Out')) return <span className="dismissal blue">{styled} (OUT)</span>;
  if (styled.includes('Retired')) return <span className="dismissal gray">{styled}</span>;
  return <span className="dismissal red">{styled} (OUT)</span>;
};

const CommentaryTab = ({ commentary, scorecard }) => {
  if (!commentary || !scorecard) return <p>No commentary available.</p>;

  // Group balls by innings and over
  const grouped = {};
  commentary
    .sort((a, b) => b.inning - a.inning || b.over - a.over || b.ball - a.ball)
    .forEach((ball) => {
      const key = `inning-${ball.inning}`;
      if (!grouped[key]) grouped[key] = {};
      if (!grouped[key][ball.over]) grouped[key][ball.over] = [];
      grouped[key][ball.over].push(ball);
    });

  const getOverSummary = (inningIndex, overNum) => {
    const score = scorecard?.[inningIndex];
    if (!score) return null;

    const batsmen = score.batting?.slice(-2).map(b => `${b.batsman.name}: ${b.r} (${b.b})`);
    const lastBowler = score.bowling?.slice(-1)[0];
    const bowler = lastBowler ? `${lastBowler.bowler.name}: ${lastBowler.o}-${lastBowler.r}-${lastBowler.w}` : 'N/A';
    const totalRuns = score?.extras?.r + score.batting?.reduce((a, b) => a + b.r, 0);
    const teamScore = `${totalRuns}/${score.batting?.length || 0}`;

    return (
      <div className="over-summary">
        <div className="over-circle">Over {overNum + 1}</div>
        <div className="summary-text">
          üèè <strong>Score:</strong> {teamScore} &nbsp;|&nbsp;
          üß¢ <strong>Batsmen:</strong> {batsmen?.join(', ')} &nbsp;|&nbsp;
          üéØ <strong>Bowler:</strong> {bowler}
        </div>
      </div>
    );
  };

  return (
    <div className="commentary-tab">
      {Object.entries(grouped).map(([inningKey, overs], idx) => (
        <div key={inningKey} className="inning-block">
          <h3>{getInningLabel(Number(inningKey.split('-')[1]))}</h3>
          {Object.entries(overs)
            .sort((a, b) => b[0] - a[0]) // latest over first
            .map(([overNum, balls]) => (
              <div key={overNum} className="over-block">
                {getOverSummary(Number(inningKey.split('-')[1]), Number(overNum))}
                {balls
                  .sort((a, b) => b.ball - a.ball) // latest ball first
                  .map((entry, idx2) => {
                    const emoji = emojiForEvent(entry);
                    const batsman = entry.batsman?.name || 'Unknown';
                    const bowler = entry.bowler?.name || 'Unknown';
                    const dismissalText = entry.dismissal ? formatDismissal(entry.dismissal) : null;

                    return (
                      <div key={idx2} className="commentary-entry">
                        <div className="ball">
                          <span className="emoji">{emoji}</span> Over {entry.over}.{entry.ball}
                        </div>
                        <div className="details">
                          <strong>{bowler}</strong> to <strong>{batsman}</strong> ‚Äì{' '}
                          {dismissalText || (
                            <>
                              <span className="runs">{entry.runs} run{entry.runs !== 1 ? 's' : ''}</span>
                              {entry.extras > 0 && (
                                <span className="extras"> + {entry.extras} extra{entry.extras !== 1 ? 's' : ''}</span>
                              )}
                            </>
                          )}
                        </div>
                      </div>
                    );
                  })}
              </div>
            ))}
        </div>
      ))}
    </div>
  );
};

export default CommentaryTab;
