import { useParams } from 'react-router-dom';
import { SeriesMatchListProvider } from './SeriesMatchListContext';
import SeriesMatchesTab from './series/SeriesMatchesTab';
import SeriesSquadsTab from './series/SeriesSquadsTab';

export default function SeriesTabsWrapper() {
  const { seriesId } = useParams();
  const [activeTab, setActiveTab] = useState('matches');

  return (
    <SeriesMatchListProvider seriesId={seriesId}>
      <div className="tab-buttons">
        <button onClick={() => setActiveTab('matches')}>Matches</button>
        <button onClick={() => setActiveTab('squads')}>Squads</button>
      </div>

      {activeTab === 'matches' && <SeriesMatchesTab />}
      {activeTab === 'squads' && <SeriesSquadsTab />}
    </SeriesMatchListProvider>
  );
}
