import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import './Players.css';

export default function Players() {
  const [players, setPlayers] = useState([]);
  const [query, setQuery] = useState('');
  const [offset, setOffset] = useState(0);

  const load = async () => {
    const url = query
      ? `/api/players/search?q=${encodeURIComponent(query)}`
      : `/api/players?offset=${offset}`;
    const res = await fetch(url);
    const data = await res.json();
    setPlayers(data.data || []);
  };

  useEffect(() => { load(); }, [query, offset]);
console.log(players);

  return (
    <div className="players-container">
      <h2>Players</h2>
      <input
        type="text"
        placeholder="Search players..."
        value={query}
        onChange={e => setQuery(e.target.value)}
      />
      <div className="players-grid">
        {players.map(p => (
          <Link key={p.id} to={`/player/${p.id}`} className="player-card">
            <img src={p.playerImg} alt={p.name} />
            <p>{p.name}</p>
            <p>{p.playerImg}</p>
            <p className="short">{p.shortname}</p>
          </Link>
        ))}
      </div>
      {!query && (
        <div className="paging">
          <button onClick={() => setOffset(o => Math.max(o - 50, 0))} disabled={offset === 0}>Prev</button>
          <button onClick={() => setOffset(o => o + 50)}>Next</button>
        </div>
      )}
    </div>
  );
}
