import React, { useEffect, useState } from 'react';
import Breadcrumb from './Breadcrumb';
import './News.css';

export default function News() {
  const [articles, setArticles] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchNews = async () => {
      try {
        const response = await fetch(`/api/news?q=cricket`);
        const json = await response.json();
        setArticles(json.articles || []);
      } catch (error) {
        console.error('Error fetching news articles', error);
      } finally {
        setLoading(false);  // âœ… stop loading
      }
    };

    fetchNews();
  }, []);

  if (loading) {
    return (
      <div style={{ padding: '20px' }}>
        <h2 className="news-title">ðŸ“° Latest Cricket News</h2>
        <p>Loading News...</p>
      </div>
    );
  }

  if (!articles || articles.length === 0) {
    <Breadcrumb items={[
      { label: 'Home', link: '/' },
      { label: 'News', link: '/news' }
    ]} />
    return (
      <div style={{ padding: '20px' }}>
        <h2 className="news-title">ðŸ“° Latest Cricket News</h2>
        <p>No articles at the moment.</p>
      </div>
    );
  }

  return (
    <>
    <Breadcrumb items={[
      { label: 'Home', link: '/' },
      { label: 'News', link: '/news' }
    ]} />
    <div className="news-container">
      <h1 className="news-title">ðŸ“° Latest Cricket News</h1>
      <div className="news-grid">
        {articles.map((article, index) => (
          <div key={index} className="news-card">
            <img
              src={article.image}
              alt={article.title}
              className="news-image"
            />
            <h2 className="news-heading">{article.title}</h2>
            <p className="news-meta">
              {article.source.name} â€¢{' '}
              {new Date(article.publishedAt).toLocaleString()}
            </p>
            <p className="news-description">{article.description}</p>
            <a
              href={article.url}
              target="_blank"
              rel="noreferrer"
              className="news-link"
            >
              Read more â†’
            </a>
          </div>
        ))}
      </div>
    </div>
    </>
  );
}
