import React from 'react';
import './PlayerModal.css';

const PlayerModal = ({ player, position, country, onClose }) => {
  return (
    <div className="modal-overlay" onClick={onClose}>
      <div className="modal-box" onClick={e => e.stopPropagation()}>
        <button className="close-button" onClick={onClose}>Ã—</button>
        <img src={player.image_path} alt={player.fullname} className="modal-player-img" />
        <h2>{player.fullname}</h2>
        <p><strong>Position:</strong> {position}</p>
        <p><strong>Country:</strong> {country?.name}</p>
        <p><strong>DOB:</strong> {player.dateofbirth}</p>
        <p><strong>Batting:</strong> {player.battingstyle}</p>
        <p><strong>Bowling:</strong> {player.bowlingstyle}</p>
      </div>
    </div>
  );
};

export default PlayerModal;
