import React from 'react';
import './ScorecardTab.css';

const ScorecardTab = ({ match }) => {
  const { batting, bowling, runs, lineup, scoreboards } = match;

  if (!batting || !runs || !lineup || bowling || scoreboards || batting.length === 0 || runs.length === 0 || lineup.length === 0 || scoreboards.length === 0 || bowling.length === 0) {
    return <p>No Scorecard available for this match.</p>;
  }

  const getPlayerName = (id) => {
    const player = lineup.find(p => p.id === id);
    return player ? player.fullname || player.displayname || 'N/A' : 'N/A';
  };

  const groupByInnings = (data, key = 'scoreboard') => {
    return data.reduce((acc, item) => {
      const inning = item[key];
      if (!acc[inning]) acc[inning] = [];
      acc[inning].push(item);
      return acc;
    }, {});
  };

  const battingByInnings = groupByInnings(batting);
  const bowlingByInnings = groupByInnings(bowling);
  const extrasByInnings = scoreboards.filter(s => s.type === 'extra');
  const totalsByInnings = scoreboards.filter(s => s.type === 'total');

  const renderDismissal = (batsman) => {
    if (batsman.catch_stump_player_id) {
      return `c ${getPlayerName(batsman.catch_stump_player_id)} b ${getPlayerName(batsman.bowling_player_id)}`;
    } else if (batsman.runout_by_id) {
      return `run out (${getPlayerName(batsman.runout_by_id)})`;
    } else if (batsman.bowling_player_id) {
      return `b ${getPlayerName(batsman.bowling_player_id)}`;
    } else {
      return batsman.active ? 'not out' : 'N/A';
    }
  };

  const renderExtras = (scoreboard) => {
    const { wide, noball_runs, bye, leg_bye } = scoreboard;
    const extraTotal = (wide || 0) + (noball_runs || 0) + (bye || 0) + (leg_bye || 0);
    return {
      text: `Extras: ${extraTotal} (w: ${wide || 0}, nb: ${noball_runs || 0}, b: ${bye || 0}, lb: ${leg_bye || 0})`,
      total: extraTotal,
    };
  };

  const renderInnings = (inningKey, index) => {
    const batters = battingByInnings[inningKey] || [];
    const bowlers = bowlingByInnings[inningKey] || [];
    const extras = extrasByInnings.find(e => e.scoreboard === inningKey);
    const totals = totalsByInnings.find(e => e.scoreboard === inningKey);
    const inningsRuns = runs[index];

    return (
      <div key={inningKey} className="innings-block">
        <h3>{index + 1}st Innings ({getPlayerName(inningsRuns?.team_id)})</h3>

        {/* Batting Table */}
        <table className="scorecard-table">
          <thead>
            <tr>
              <th>Batsman</th>
              <th>Dismissal</th>
              <th>R</th>
              <th>B</th>
              <th>4s</th>
              <th>6s</th>
              <th>SR</th>
            </tr>
          </thead>
          <tbody>
            {batters.map(b => (
              <tr key={b.id}>
                <td>{getPlayerName(b.player_id)}</td>
                <td>{renderDismissal(b)}</td>
                <td>{b.score}</td>
                <td>{b.ball}</td>
                <td>{b.four_x}</td>
                <td>{b.six_x}</td>
                <td>{b.rate}</td>
              </tr>
            ))}
            {extras && (
              <tr>
                <td colSpan="7">{renderExtras(extras).text}</td>
              </tr>
            )}
            {totals && (
              <tr>
                <td colSpan="7">
                  <strong>Total: {totals.total} / {totals.wickets} in {totals.overs} overs</strong>
                </td>
              </tr>
            )}
          </tbody>
        </table>

        {/* Bowling Table */}
        <h4>Bowling</h4>
        <table className="scorecard-table">
          <thead>
            <tr>
              <th>Bowler</th>
              <th>O</th>
              <th>M</th>
              <th>R</th>
              <th>W</th>
              <th>NB</th>
              <th>WD</th>
              <th>Econ</th>
            </tr>
          </thead>
          <tbody>
            {bowlers.map(b => (
              <tr key={b.id}>
                <td>{getPlayerName(b.player_id)}</td>
                <td>{b.overs}</td>
                <td>{b.medians}</td>
                <td>{b.runs}</td>
                <td>{b.wickets}</td>
                <td>{b.noball}</td>
                <td>{b.wide}</td>
                <td>{b.rate}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    );
  };

  const inningsKeys = Object.keys(battingByInnings).sort(); // e.g., ["S1", "S2"]

  return (
    <div className="scorecard-tab">
      {inningsKeys.map((key, index) => renderInnings(key, index))}
    </div>
  );
};

export default ScorecardTab;
