import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import './Series.css';

export default function Series() {
  const [seriesList, setSeriesList] = useState([]);
  const [matches, setMatches] = useState([]);
  const [selectedSeries, setSelectedSeries] = useState(null);
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    const fetchSeries = async () => {
      try {
        const res = await fetch('/api/series');
        const json = await res.json();
        setSeriesList(json.data || []);
      } catch (err) {
        console.error('Failed to load series:', err);
      }
    };
    fetchSeries();
  }, []);

  const loadMatches = async (seriesId) => {
    setSelectedSeries(seriesId);
    setLoading(true);
    try {
      const res = await fetch(`/api/series/${seriesId}/matches`);
      const json = await res.json();
      setMatches(json.data?.matchList || []);
    } catch (err) {
      console.error('Failed to load matches for series', err);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="series-container">
      <h2>{selectedSeries ? 'Fixtures' : 'All Series'}</h2>

      {!selectedSeries ? (
        <div className="series-grid">
          {seriesList.map((s) => (
            <div key={s.id} className="series-card" onClick={() => loadMatches(s.id)}>
              <h3>{s.name}</h3>
              <p>{s.startDate} - {s.endDate}</p>
            </div>
          ))}
        </div>
      ) : (
        <>
          <button onClick={() => setSelectedSeries(null)}>‚Üê Back to Series</button>
          {loading ? (
            <p>Loading fixtures...</p>
          ) : (
            matches.length > 0 ? (
              <div className="fixtures-list">
                {matches.map(match => (
                  <Link key={match.id} to={`/match/${match.id}`} state={{ from: 'series', seriesId: selectedSeries }}>
                    <div className="fixture-card">
                      <strong>{match.teams}</strong>
                      <p>{match.date}</p>
                      <p>{match.status}</p>
                    </div>
                  </Link>
                ))}
              </div>
            ) : (
              <p>No fixtures found for this series.</p>
            )
          )}
        </>
      )}
    </div>
  );
}
