import React, { useEffect, useState } from 'react';
import './InfoTab.css';

const InfoTab = ({ match }) => {
  if (!match) return null;

  const {
    series_id,
    matchType,
    tossWinner,
    tossChoice,
    date,
    venue,
    status,
  } = match;

  const [seriesInfo, setSeriesInfo] = useState(null);

  useEffect(() => {
    const fetchSeriesInfo = async () => {
      try {
        const res = await fetch(`/api/series/${seriesId}/matches`);
        const json = await res.json();
        setSeriesInfo(json.data?.info || []);
      } catch (err) {
        console.error('Error loading series details', err);
      } finally {
