import React, { createContext, useContext, useEffect, useState } from 'react';
import axios from 'axios';

const SeriesMatchListContext = createContext();

export const SeriesMatchListProvider = ({ seriesId, children }) => {
  const [seriesMatches, setSeriesMatches] = useState([]);
  const [loadingMatches, setLoadingMatches] = useState(true);

  useEffect(() => {
    if (!seriesId) return;

    const fetchSeriesMatches = async () => {
      setLoadingMatches(true);
      try {
        const res = await axios.get(`/api/series/${seriesId}/matches`);
        setSeriesMatches(res.data?.data?.data || []);
      } catch (err) {
        console.error(`Error fetching /api/series/${seriesId}/matches`, err);
        setSeriesMatches([]);
      } finally {
        setLoadingMatches(false);
      }
    };

    fetchSeriesMatches();
  }, [seriesId]);

  return (
    <SeriesMatchListContext.Provider value={{ seriesMatches, loadingMatches }}>
      {children}
    </SeriesMatchListContext.Provider>
  );
};

export const useSeriesMatchList = () => useContext(SeriesMatchListContext);
