import React from 'react';
import './PartnershipTab.css';

const PartnershipTab = ({ commentary }) => {
  if (!commentary || commentary.length === 0) {
    return <div className="partnership-tab">No data available</div>;
  }

  const partnershipsByInning = {};

  commentary.forEach((entry, index) => {
    const inning = entry.inning;
    if (!partnershipsByInning[inning]) {
      partnershipsByInning[inning] = {
        partnerships: [],
        current: {
          batsmen: [],
          runs: 0,
          balls: 0,
        },
      };
    }

    const current = partnershipsByInning[inning].current;

    const batsmanId = entry.batsman?.id;
    const batsmanName = entry.batsman?.name;
    const isNewBatsman = batsmanId && !current.batsmen.find(b => b.id === batsmanId);

    if (isNewBatsman) {
      if (current.batsmen.length === 2) {
        // Save previous partnership
        partnershipsByInning[inning].partnerships.push({
          batsmen: [...current.batsmen],
          runs: current.runs,
          balls: current.balls,
        });
        // Start new partnership with one previous + new batsman
        current.batsmen = [current.batsmen[1], { id: batsmanId, name: batsmanName }];
        current.runs = 0;
        current.balls = 0;
      } else {
        current.batsmen.push({ id: batsmanId, name: batsmanName });
      }
    }

    current.runs += entry.runs || 0;
    current.runs += entry.extras || 0;

    if (!entry.penalty || entry.penalty === '') {
      current.balls += 1;
    }

    if (entry.dismissal) {
      // Save partnership and reset
      partnershipsByInning[inning].partnerships.push({
        batsmen: [...current.batsmen],
        runs: current.runs,
        balls: current.balls,
      });
      // Replace out batsman with placeholder
      current.batsmen = current.batsmen.filter(b => b.id !== batsmanId);
      current.runs = 0;
      current.balls = 0;
    }
  });

  // Save final incomplete partnerships
  Object.values(partnershipsByInning).forEach(obj => {
    const { current, partnerships } = obj;
    if (current.batsmen.length === 2) {
      partnerships.push({
        batsmen: [...current.batsmen],
        runs: current.runs,
        balls: current.balls,
      });
    }
  });

  return (
    <div className="partnership-tab">
      {Object.entries(partnershipsByInning).map(([inning, data]) => (
        <div key={inning}>
          <h3>Inning {parseInt(inning) + 1}</h3>
          <table>
            <thead>
              <tr>
                <th>Batsmen</th>
                <th>Runs</th>
                <th>Balls</th>
              </tr>
            </thead>
            <tbody>
              {data.partnerships.map((p, idx) => (
                <tr key={idx}>
                  <td>{p.batsmen.map(b => b.name).join(' & ')}</td>
                  <td>{p.runs}</td>
                  <td>{p.balls}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      ))}
    </div>
  );
};

export default PartnershipTab;
