// src/pages/Series.js
import React, { useEffect, useState } from 'react';
import { useParams, useLocation, useNavigate, Link } from 'react-router-dom';
import SeriesSquad from './SeriesSquad';
import SeriesNews from './SeriesNews';
import Breadcrumb from './Breadcrumb';
import './Series.css';

const Series = () => {
  const { id } = useParams();
  const [seriesList, setSeriesList] = useState([]);
  const [matches, setMatches] = useState([]);
  const [pointsTable, setPointsTable] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [loading, setLoading] = useState(false);
  const [activeTab, setActiveTab] = useState('fixtures');
  const [offset, setOffset] = useState(0);
  const [seriesInfo, setSeriesInfo] = useState([]);
  const [fallbackImg, setFallbackImg] = useState(null);

  const breadcrumbSeries = [
    { label: 'Home', link: '/' },
    { label: 'Series', link: '/series' },
  ];

  const navigate = useNavigate();
  const location = useLocation();
  const state = location.state || {};

  const getOrdinal = (n) => {
    const s = ["th", "st", "nd", "rd"],
      v = n % 100;
    return n + (s[(v - 20) % 10] || s[v] || s[0]);
  };

  const formatFullDate = (input, fallbackYear = null, startMonth = null) => {
    if (!input) return 'Invalid date';

    let date;

    if (/^\d{4}-\d{2}-\d{2}$/.test(input)) {
      date = new Date(input);
    } else if (/^[A-Za-z]{3,9} \d{1,2}$/.test(input) && fallbackYear !== null) {
      const tempDate = new Date(`${input} ${fallbackYear}`);
      const endMonth = tempDate.getMonth();
      const adjustedYear = (startMonth !== null && endMonth < startMonth) ? fallbackYear + 1 : fallbackYear;
      date = new Date(`${input} ${adjustedYear}`);
    } else {
      date = new Date(input);
    }

    if (isNaN(date)) return 'Invalid date';

    const day = getOrdinal(date.getDate());
    const month = date.toLocaleString('default', { month: 'long' });
    const year = date.getFullYear();

    return `${month} ${day}, ${year}`;
  };

  const formattedDate = (input) => {
    if (!input) return 'N/A';
    try {
      return new Date(input).toLocaleString();
    } catch (e) {
      return 'Invalid date';
    }
  };

  useEffect(() => {
    if (!id) fetchSeriesList();
    else loadSeriesDetails(id);
  }, [id]);

  const fetchSeriesList = async (term = '', newOffset = 0) => {
    setLoading(true);
    try {
      const res = await fetch(`/api/series?search=${encodeURIComponent(term)}&offset=${newOffset}`);
      const json = await res.json();
      setSeriesList(json.data || []);
    } catch (err) {
      console.error('Error loading series list', err);
      setSeriesList([]);
    } finally {
      setLoading(false);
    }
  };

  const loadSeriesDetails = async (seriesId) => {
    setLoading(true);
    try {
      const res1 = await fetch(`/api/series/${seriesId}/matches`);
      const json1 = await res1.json();
      setMatches(json1.data?.matchList || []);
      setSeriesInfo(json1.data?.info || []);

      const res2 = await fetch(`/api/series/${seriesId}/points`);
      const json2 = await res2.json();
      setPointsTable(json2.data || []);
    } catch (err) {
      console.error('Error loading series details', err);
    } finally {
      setLoading(false);
    }
  };

  const seriesName = seriesInfo.name;

  const breadcrumbSeriesTabs = [
    { label: 'Home', link: '/' },
    { label: 'Series', link: '/series' },
    { label: seriesName, link: `/series/${seriesName}` },
  ];

  const handleSearchChange = (e) => {
    const term = e.target.value;
    setSearchTerm(term);
    setOffset(0);
    fetchSeriesList(term, 0);
  };

  const handlePrev = () => {
    const newOffset = Math.max(offset - 10, 0);
    setOffset(newOffset);
    fetchSeriesList(searchTerm, newOffset);
  };

  const handleNext = () => {
    const newOffset = offset + 10;
    setOffset(newOffset);
    fetchSeriesList(searchTerm, newOffset);
  };

  useEffect(() => {
    const fetchFallback = async () => {
      try {
        const res = await fetch('/api/default-images');
        const text = await res.text();
        const match = text.match(/flag_url:\s*['"]([^'"]+)['"]/);
        if (match) setFallbackImg(match[1]);
      } catch (err) {
        console.error('Failed to fetch fallback image:', err);
      }
    };

    fetchFallback();
  }, []);

  if (!id) {
    return (
      <div>
      <Breadcrumb items={breadcrumbSeries} />
      <div className="series-container">
        <h2>All Series</h2>
        <input
          type="text"
          placeholder="Search series..."
          value={searchTerm}
          onChange={handleSearchChange}
          className="series-search"
        />

        {loading ? (
          <p>Loading series...</p>
        ) : seriesList.length === 0 ? (
          <p>No series found.</p>
        ) : (
          <>
            <div className="series-grid">
              {seriesList.map((s) => (
                <div
                  key={s.id}
                  className="series-card"
                  onClick={() =>
                    navigate(`/series/${s.id}`, {
                      state: { from: 'Series', seriesName: s.name }
                    })
                  }
                >
                  <h3>{s.name}</h3>
                  <p>Start Date - {formatFullDate(s.startDate)}</p>
                  <p>End Date - {formatFullDate(s.endDate, new Date(s.startDate).getFullYear(), new Date(s.startDate).getMonth())}</p>
                  <p>TEST - {s.test}, ODI - {s.odi}, T20 - {s.t20}</p>
                  <p>Total Matches - {(s.test || 0) + (s.odi || 0) + (s.t20 || 0)}</p>
                </div>
              ))}
            </div>
            <div className="series-pagination">
              <button disabled={offset === 0} onClick={handlePrev}>← Prev</button>
              <button onClick={handleNext}>Next →</button>
            </div>
          </>
        )}
      </div>
      </div>
    );
  }

  return (
      <div>
      <Breadcrumb items={breadcrumbSeriesTabs} />
    <div className="series-container">
      <div className="tabs">
        <button onClick={() => setActiveTab('fixtures')} className={activeTab === 'fixtures' ? 'active' : ''}>Fixtures</button>
        <button onClick={() => setActiveTab('points')} className={activeTab === 'points' ? 'active' : ''}>Points Table</button>
        <button onClick={() => setActiveTab('squad')} className={activeTab === 'squad' ? 'active' : ''}>Squads</button>
        <button onClick={() => setActiveTab('news')} className={activeTab === 'news' ? 'active' : ''}>News</button>
      </div>

      {loading ? (
        <p>Loading...</p>
      ) : activeTab === 'fixtures' ? (
        matches.length > 0 ? (
          <div className="fixtures-list">
            {matches.map(match => (
              <Link key={match.id} to={`/match/${match.id}`} className="fixture-link" state={{ from: 'Series' }}>
                <div className="fixture-card">
                  <strong>{match.name}</strong>
                  <p>{match.venue}</p>
                  {match.status === 'Match not started' && (
                    <p>Match starts at {formattedDate(match.dateTimeGMT)}</p>
                  )}
                  {match.status !== 'Match not started' && (
                    <p>Date/Time: {formattedDate(match.dateTimeGMT)}</p>
                  )}
                  {match.status !== 'Match not started' && (
                    <p>{match.status}</p>
                  )}
                </div>
              </Link>
            ))}
          </div>
        ) : <p>No matches found.</p>
      ) : activeTab === 'points' ? (
        pointsTable.length > 0 ? (
          <table className="points-table">
            <thead>
              <tr>
                <th>Team</th>
                <th>M</th>
                <th>W</th>
                <th>L</th>
                <th>T</th>
                <th>NR</th>
              </tr>
            </thead>
            <tbody>
              {pointsTable.map(team => (
                <tr key={team.name}>
                  <td>
                    <img
                      src={
                        team.img && team.img.includes('h.cricapi.com')
                          ? fallbackImg || '' // wait for fallbackImg
                          : team.img
                      }
                      alt={team.name}
                      width={24}
                    /> {team.teamname}
                  </td>
                  <td>{team.matches}</td>
                  <td>{team.wins}</td>
                  <td>{team.loss}</td>
                  <td>{team.ties}</td>
                  <td>{team.nr}</td>
                </tr>
              ))}
            </tbody>
          </table>
        ) : <p>No points table available.</p>
      ) : activeTab === 'squad' ? (
        <SeriesSquad />
      ) : (
        <SeriesNews seriesName={seriesInfo.name}/>
      )}
    </div>
    </div>
  );
};

export default Series;
