import React, { useEffect, useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import './Schedule.css';

const Schedule = () => {
  const [matches, setMatches] = useState([]);
  const [activeTab, setActiveTab] = useState('live');
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchMatches = async () => {
      try {
        const res = await fetch('/api/live');
        const json = await res.json();
        setMatches(json.data || []);
      } catch (err) {
        console.error('Error fetching matches:', err);
      } finally {
        setLoading(false);
      }
    };

    fetchMatches();
  }, []);

  const getFilteredMatches = () => {
    if (activeTab === 'upcoming') {
      return matches.filter(m => m.status === 'Match not started');
    } else if (activeTab === 'completed') {
      return matches.filter(m => m.matchStarted && m.matchEnded);
    } else {
      return matches.filter(
        m =>
          m.matchStarted &&
          !m.matchEnded &&
          m.status !== 'Match not started'
      );
    }
  };

  const renderInningsScore = (scoreArray) => {
    if (!Array.isArray(scoreArray)) return null;

    return scoreArray.map((s, i) => (
      <p key={i} className="innings-score">
        {s.inning}: {s.r}/{s.w} ({s.o} ov)
      </p>
    ));
  };

  const renderMatchCard = (match) => {
    return (
      <div key={match.id} className="match-card" onClick={() => navigate(`/match/${match.id}`)}>
        <div className="match-header">
          <h4>{match.name}</h4>
          <p className="venue">{match.venue}</p>
        </div>

        <div className="teams">
          {match.teamInfo?.map((team, idx) => (
            <div className="team" key={idx}>
              <img src={team.img} alt={team.shortname} />
              <span>{team.shortname}</span>
            </div>
          ))}
        </div>

        {match.score?.length > 0 && (
          <div className="score-box">
            {renderInningsScore(match.score)}
          </div>
        )}

        {match.status !== 'Match not started' && (
          <p className="status">{match.status}</p>
        )}        

        {match.series_id && (
          <Link
            to={`/series/${match.series_id}`}
            className="series-link"
            onClick={e => e.stopPropagation()}
            state={{ fromHome: true }}
          >
            {match.seriesName || 'Series'}
          </Link>
        )}
      </div>
    );
  };

  const filteredMatches = getFilteredMatches();

  return (
    <div className="schedule-container">
      <h2>Match Schedule</h2>

      <div className="schedule-tabs">
        <button className={activeTab === 'live' ? 'active' : ''} onClick={() => setActiveTab('live')}>Live</button>
        <button className={activeTab === 'upcoming' ? 'active' : ''} onClick={() => setActiveTab('upcoming')}>Upcoming</button>
        <button className={activeTab === 'completed' ? 'active' : ''} onClick={() => setActiveTab('completed')}>Completed</button>
      </div>

      {loading ? (
        <p>Loading matches...</p>
      ) : (
        <div className="schedule-list">
          {filteredMatches.length === 0 ? (
            <p>No matches found.</p>
          ) : (
            filteredMatches.map(renderMatchCard)
          )}
        </div>
      )}
    </div>
  );
};

export default Schedule;
