import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import './css/TeamMatches.css';

const TeamMatches = () => {
  const { id } = useParams(); // team ID from route
  const [matches, setMatches] = useState([]);
  const [teamNames, setTeamNames] = useState({});
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchMatches = async () => {
      try {
        const res = await axios.get(`/api/teams/${id}/matches`);
        const fixtures = res.data.data.fixtures || [];
        setMatches(fixtures);

        const teamIds = new Set();
        fixtures.forEach(m => {
          teamIds.add(m.localteam_id);
          teamIds.add(m.visitorteam_id);
        });

        const names = {};
        for (const teamId of teamIds) {
          try {
            const teamRes = await axios.get(`/api/teams/${teamId}`);
console.log(teamRes.data);
            names[teamId] = teamRes.data.data.name;
          } catch {
            names[teamId] = 'Unknown';
          }
        }
        setTeamNames(names);
      } catch (err) {
        console.error('Failed to fetch team matches', err);
      } finally {
        setLoading(false);
      }
    };

    fetchMatches();
  }, [id]);

  const getTeamName = (teamId) => teamNames[teamId] || '...';
console.log(teamNames);
  const categorizeMatches = () => {
    const upcoming = [];
    const live = [];
    const completed = [];

    matches.forEach(match => {
      if (match.status === 'NS' || match.status === 'Postp.') {
        upcoming.push(match);
      } else if (
        ['1st Innings', '2nd Innings', '3rd Innings', '4th Innings', 'Innings Break', 'Tea Break', 'Lunch', 'Dinner', 'Int.', 'Delayed'].includes(match.status)
      ) {
        live.push(match);
      } else {
        completed.push(match);
      }
    });

    return { upcoming, live, completed };
  };

  const { upcoming, live, completed } = categorizeMatches();

  const renderMatch = (match) => (
    <div key={match.id} className="match-card">
      <p>
        <strong>{getTeamName(match.localteam_id)}</strong> vs{' '}
        <strong>{getTeamName(match.visitorteam_id)}</strong>
      </p>
      <p>Status: {match.status}</p>
      <p>Start: {new Date(match.starting_at).toLocaleString()}</p>
    </div>
  );

  if (loading) return <p>Loading matches...</p>;

  return (
    <div className="team-matches">
      <h2>Team Matches</h2>

      <section>
        <h3>ðŸ”´ Live</h3>
        {live.length ? live.map(renderMatch) : <p>No live matches.</p>}
      </section>

      <section>
        <h3>ðŸ•’ Upcoming</h3>
        {upcoming.length ? upcoming.map(renderMatch) : <p>No upcoming matches.</p>}
      </section>

      <section>
        <h3>âœ… Completed</h3>
        {completed.length ? completed.map(renderMatch) : <p>No completed matches.</p>}
      </section>
    </div>
  );
};

export default TeamMatches;
