import React, { useEffect, useState } from 'react';
import './css/Players.css';
import { useNavigate } from 'react-router-dom';

const GITHUB_URL = 'https://raw.githubusercontent.com/ashwint87/cricket-website/master/';
const PAGE_SIZE = 30;

export default function Players() {
  const [players, setPlayers] = useState([]);
  const [countries, setCountries] = useState({});
  const [filtered, setFiltered] = useState([]);
  const [search, setSearch] = useState('');
  const [selectedCountry, setSelectedCountry] = useState('');
  const [selectedPosition, setSelectedPosition] = useState('');
  const [selectedBatStyle, setSelectedBatStyle] = useState('');
  const [sort, setSort] = useState('');
  const [currentPage, setCurrentPage] = useState(0);
  const navigate = useNavigate();

  useEffect(() => {
    const load = async () => {
      const res = await fetch(`${GITHUB_URL}players_data.json`);
      const data = await res.json();
      setPlayers(data);
    };
    load();
  }, []);

  useEffect(() => {
    const apply = async () => {
      let temp = [...players];

      if (search) {
        const term = search.toLowerCase();
        temp = temp.filter(p =>
          `${p.firstname} ${p.lastname}`.toLowerCase().includes(term)
        );
      }

      if (selectedCountry) {
        temp = temp.filter(p => p.country_id === parseInt(selectedCountry));
      }

      if (selectedPosition) {
        temp = temp.filter(p => p.position?.name === selectedPosition);
      }

      if (selectedBatStyle) {
        temp = temp.filter(p => p.battingstyle === selectedBatStyle);
      }

      if (sort === 'name_asc') {
        temp.sort((a, b) => a.fullname.localeCompare(b.fullname));
      } else if (sort === 'name_desc') {
        temp.sort((a, b) => b.fullname.localeCompare(a.fullname));
      }

      setFiltered(temp);
      setCurrentPage(0);
    };
    apply();
  }, [players, search, selectedCountry, selectedPosition, selectedBatStyle, sort]);

  useEffect(() => {
    const loadCountryNames = async () => {
      const ids = [...new Set(players.map(p => p.country_id))];
      const countryMap = {};
      for (let id of ids) {
        if (!countryMap[id]) {
          try {
            const res = await fetch(`/api/countries/${id}`);
            const json = await res.json();
            countryMap[id] = json?.data?.name || 'Unknown';
          } catch {
            countryMap[id] = 'Unknown';
          }
        }
      }
      setCountries(countryMap);
    };

    if (players.length) loadCountryNames();
  }, [players]);

  const paginatedPlayers = filtered.slice(currentPage * PAGE_SIZE, (currentPage + 1) * PAGE_SIZE);

  const positions = [...new Set(players.map(p => p.position?.name).filter(Boolean))];
  const batStyles = [...new Set(players.map(p => p.battingstyle).filter(Boolean))];

  return (
    <div className="players-list-container">
      <h2>Players</h2>
      <div className="filters">
        <input
          placeholder="Search by name..."
          value={search}
          onChange={e => setSearch(e.target.value)}
        />

        <select value={selectedCountry} onChange={e => setSelectedCountry(e.target.value)}>
          <option value="">All Countries</option>
          {Object.entries(countries).map(([id, name]) => (
            <option key={id} value={id}>{name}</option>
          ))}
        </select>

        <select value={selectedPosition} onChange={e => setSelectedPosition(e.target.value)}>
          <option value="">All Positions</option>
          {positions.map(pos => (
            <option key={pos} value={pos}>{pos}</option>
          ))}
        </select>

        <select value={selectedBatStyle} onChange={e => setSelectedBatStyle(e.target.value)}>
          <option value="">All Batting Styles</option>
          {batStyles.map(style => (
            <option key={style} value={style}>{style}</option>
          ))}
        </select>

        <select value={sort} onChange={e => setSort(e.target.value)}>
          <option value="">Sort</option>
          <option value="name_asc">Name A-Z</option>
          <option value="name_desc">Name Z-A</option>
        </select>
      </div>

      <div className="players-grid">
        {paginatedPlayers.map(player => (
          <div key={player.id} className="player-card" onClick={() => navigate(`/player/${player.id}`)}>
            <img src={player.image_path} alt={player.fullname} />
            <h4>{player.fullname}</h4>
            <p>{countries[player.country_id] || 'Loading...'}</p>
          </div>
        ))}
      </div>

      <div className="pagination">
        <button disabled={currentPage === 0} onClick={() => setCurrentPage(p => p - 1)}>Prev</button>
        <span>Page {currentPage + 1} of {Math.ceil(filtered.length / PAGE_SIZE)}</span>
        <button disabled={(currentPage + 1) * PAGE_SIZE >= filtered.length} onClick={() => setCurrentPage(p => p + 1)}>Next</button>
      </div>
    </div>
  );
}
