// src/pages/Stadiums.js
import React, { useEffect, useState } from 'react';
import './Stadiums.css';

const PAGE_SIZE = 16;

const Stadiums = () => {
  const [stadiums, setStadiums] = useState([]);
  const [countries, setCountries] = useState({});
  const [searchTerm, setSearchTerm] = useState('');
  const [filteredStadiums, setFilteredStadiums] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const [venuesRes, countriesRes] = await Promise.all([
          fetch('/api/venues'),
          fetch('/api/countries')
        ]);
        const venuesData = await venuesRes.json();
        const countriesData = await countriesRes.json();

        const countryMap = {};
        countriesData.data?.forEach(c => {
          countryMap[c.id] = c.name;
        });

        setStadiums(venuesData.data || []);
        setCountries(countryMap);
        setFilteredStadiums(venuesData.data || []);
      } catch (err) {
        console.error('Error fetching stadiums:', err);
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  const handleSearch = (e) => {
    const term = e.target.value.toLowerCase();
    setSearchTerm(term);
    setCurrentPage(1);

    const filtered = stadiums.filter((venue) => {
      const countryName = countries[venue.country_id]?.toLowerCase() || '';
      return (
        venue.name?.toLowerCase().includes(term) ||
        venue.city?.toLowerCase().includes(term) ||
        countryName.includes(term)
      );
    });

    setFilteredStadiums(filtered);
  };

  const paginatedStadiums = filteredStadiums.slice(
    (currentPage - 1) * PAGE_SIZE,
    currentPage * PAGE_SIZE
  );

  return (
    <div className="stadiums-container">
      <h2>Stadiums</h2>
      <input
        type="text"
        placeholder="Search by name, city or country"
        value={searchTerm}
        onChange={handleSearch}
        className="stadium-search"
      />

      {loading ? (
        <p>Loading stadiums...</p>
      ) : (
        <>
          <div className="stadium-grid">
            {paginatedStadiums.map((venue) => (
              <div className="stadium-card" key={venue.id}>
                <img src={venue.image_path} alt={venue.name} />
                <h3>{venue.name}</h3>
                <p><strong>City:</strong> {venue.city}</p>
                <p><strong>Country:</strong> {countries[venue.country_id] || '--'}</p>
                <p><strong>Capacity:</strong> {venue.capacity || 'N/A'}</p>
              </div>
            ))}
          </div>

          <div className="pagination">
            <button
              onClick={() => setCurrentPage((prev) => Math.max(prev - 1, 1))}
              disabled={currentPage === 1}
            >
              Previous
            </button>
            <span>Page {currentPage}</span>
            <button
              onClick={() =>
                setCurrentPage((prev) =>
                  prev * PAGE_SIZE < filteredStadiums.length ? prev + 1 : prev
                )
              }
              disabled={currentPage * PAGE_SIZE >= filteredStadiums.length}
            >
              Next
            </button>
          </div>
        </>
      )}
    </div>
  );
};

export default Stadiums;
