import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { FixedSizeList as List } from 'react-window';
import PlayerModal from './PlayerModal';
import './Players.css';

const Players = () => {
  const [players, setPlayers] = useState([]);
  const [displayedPlayers, setDisplayedPlayers] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [countries, setCountries] = useState({});
  const [positions, setPositions] = useState({});
  const [selectedPlayer, setSelectedPlayer] = useState(null);


useEffect(() => {
  const fetchData = async () => {
    try {
      console.log('📡 Fetching players, countries, positions...');
      const [playersRes, countriesRes, positionsRes] = await Promise.all([
        axios.get('/api/players'),
        axios.get('/api/countries'),
        axios.get('/api/positions'),
      ]);

      // Map countries
      const countriesMap = {};
      (countriesRes.data || []).forEach(c => {
        countriesMap[c.id] = c;
      });
      setCountries(countriesMap);

      // Map positions
      const positionsMap = {};
      (positionsRes.data || []).forEach(p => {
        positionsMap[p.id] = p.name;
      });
      setPositions(positionsMap);

      const allPlayers = playersRes.data?.data || [];
      const limitedPlayers = allPlayers.slice(0, 10); // Show first 10 only
      setPlayers(allPlayers);
      setDisplayedPlayers(limitedPlayers);

      console.log('✅ Players loaded:', limitedPlayers.length);
    } catch (err) {
      console.error('❌ Error loading players:', err);
    }
  };

  fetchData();
}, []);

  const loadMorePlayers = () => {
    const currentLength = displayedPlayers.length;
    const nextBatch = players.slice(currentLength, currentLength + 10);
    setDisplayedPlayers([...displayedPlayers, ...nextBatch]);
  };

  const filtered = searchTerm
    ? players.filter(p => p.fullname.toLowerCase().includes(searchTerm.toLowerCase()))
    : displayedPlayers;

  const Row = ({ index, style }) => {
    const player = filtered[index];
    const country = countries[player.country_id];
    const flag = country?.image_path;
    const position = positions[player.position?.id];

    return (
      <div
        style={style}
        className="player-card"
        onClick={() => setSelectedPlayer(player)}
      >
        <img src={player.image_path} alt={player.fullname} />
        <div className="player-info">
          <h3>{player.fullname}</h3>
          <p>{position}</p>
          <p>
            {flag && <img src={flag} alt="flag" className="flag" />} {country?.name}
          </p>
        </div>
      </div>
    );
  };

  return (
    <div className="players-container">
      <input
        type="text"
        placeholder="Search player by name..."
        value={searchTerm}
        onChange={e => setSearchTerm(e.target.value)}
        className="player-search"
      />

      <List
        height={600}
        itemCount={filtered.length}
        itemSize={130}
        width={'100%'}
      >
        {Row}
      </List>

      {!searchTerm && displayedPlayers.length < players.length && (
        <button onClick={loadMorePlayers} className="load-more-button">
          Load More
        </button>
      )}

      {selectedPlayer && (
        <PlayerModal
          player={selectedPlayer}
          position={positions[selectedPlayer.position?.id]}
          country={countries[selectedPlayer.country_id]}
          onClose={() => setSelectedPlayer(null)}
        />
      )}
    </div>
  );
};

export default Players;
