import React, { useEffect, useState } from 'react';
import './css/Players.css';
import { useNavigate } from 'react-router-dom';
import debounce from 'lodash.debounce';

const GITHUB_URL = 'https://raw.githubusercontent.com/ashwint87/cricket-website/master/';

export default function Players() {
  const [players, setPlayers] = useState([]);
  const [filteredPlayers, setFilteredPlayers] = useState([]);
  const [search, setSearch] = useState('');
  const [selectedCountry, setSelectedCountry] = useState('');
  const [selectedBatStyle, setSelectedBatStyle] = useState('');
  const [selectedPosition, setSelectedPosition] = useState('');
  const [sort, setSort] = useState('');
  const [page, setPage] = useState(0);
  const [currentPage, setCurrentPage] = useState(0);
  const perPage = 30;

  const navigate = useNavigate();

useEffect(() => {
  fetch(GITHUB_URL + 'players_data.json')
    .then(res => res.json())
    .then(data => {
      const playerArray = Array.isArray(data) ? data : data.data;
      if (Array.isArray(playerArray)) {
        setPlayers(playerArray);
        setFilteredPlayers(playerArray);
      } else {
        console.error('Invalid player data format');
      }
    })
    .catch(err => console.error('Fetch error:', err));
}, []);


useEffect(() => {
  if (!players.length) return;

  const term = search.toLowerCase();
  const filtered = players.filter(p => {
    const fullName = `${p.firstname} ${p.lastname}`.toLowerCase();
    const countryMatch = selectedCountry ? p.country_id === parseInt(selectedCountry) : true;
    const batMatch = selectedBatStyle ? p.battingstyle === selectedBatStyle : true;
    const posMatch = selectedPosition ? p.position?.name === selectedPosition : true;
    return fullName.includes(term) && countryMatch && batMatch && posMatch;
  });

  let sorted = [...filtered];
  if (sort === 'name_asc') {
    sorted.sort((a, b) => a.fullname.localeCompare(b.fullname));
  } else if (sort === 'name_desc') {
    sorted.sort((a, b) => b.fullname.localeCompare(a.fullname));
  }

  setFilteredPlayers(sorted);
  setCurrentPage(0); // reset page to first
}, [players, search, selectedCountry, selectedBatStyle, selectedPosition, sort]);

  const applyFilters = debounce(() => {
    const term = search.toLowerCase();
    const filtered = players.filter(p => {
      const fullName = `${p.firstname} ${p.lastname}`.toLowerCase();
      const countryMatch = selectedCountry ? p.country_id === parseInt(selectedCountry) : true;
      const batMatch = selectedBatStyle ? p.battingstyle === selectedBatStyle : true;
      const posMatch = selectedPosition ? p.position?.name === selectedPosition : true;
      return fullName.includes(term) && countryMatch && batMatch && posMatch;
    });

    let finalList = [...filtered];
    if (sort === 'name_asc') {
      finalList.sort((a, b) => a.fullname.localeCompare(b.fullname));
    } else if (sort === 'name_desc') {
      finalList.sort((a, b) => b.fullname.localeCompare(a.fullname));
    }

    setFilteredPlayers(finalList);
    setPage(0); // reset to first page
  }, 300);

  useEffect(() => {
    applyFilters();
  }, [search, selectedCountry, selectedBatStyle, selectedPosition, sort]);

  const countries = Array.from(new Set(players.map(p => p.country_id))).filter(Boolean);
  const positions = Array.from(new Set(players.map(p => p.position?.name))).filter(Boolean);
  const batStyles = Array.from(new Set(players.map(p => p.battingstyle))).filter(Boolean);

  const paginatedPlayers = filteredPlayers.slice(page * perPage, (page + 1) * perPage);

  return (
    <div className="players-list-container">
      <h2>Players</h2>
      <div className="filters">
        <input
          type="text"
          placeholder="Search by name"
          value={search}
          onChange={e => setSearch(e.target.value)}
        />
        <select onChange={e => setSelectedCountry(e.target.value)} value={selectedCountry}>
          <option value="">All Countries</option>
          {countries.map(id => (
            <option key={id} value={id}>Country {id}</option>
          ))}
        </select>
        <select onChange={e => setSelectedBatStyle(e.target.value)} value={selectedBatStyle}>
          <option value="">All Batting Styles</option>
          {batStyles.map(style => (
            <option key={style} value={style}>{style}</option>
          ))}
        </select>
        <select onChange={e => setSelectedPosition(e.target.value)} value={selectedPosition}>
          <option value="">All Positions</option>
          {positions.map(pos => (
            <option key={pos} value={pos}>{pos}</option>
          ))}
        </select>
        <select onChange={e => setSort(e.target.value)} value={sort}>
          <option value="">Sort</option>
          <option value="name_asc">Name A-Z</option>
          <option value="name_desc">Name Z-A</option>
        </select>
      </div>

      <div className="players-grid">
        {paginatedPlayers.map(player => (
          <div key={player.id} className="player-card" onClick={() => navigate(`/player/${player.id}`)}>
            <img src={player.image_path} alt={player.fullname} />
            <div className="player-name">{player.fullname}</div>
            <div className="player-country">Country ID: {player.country_id}</div>
          </div>
        ))}
      </div>

      <div className="pagination">
        <button disabled={page === 0} onClick={() => setPage(p => p - 1)}>Previous</button>
        <span> Page {page + 1} </span>
        <button
          disabled={(page + 1) * perPage >= filteredPlayers.length}
          onClick={() => setPage(p => p + 1)}
        >
          Next
        </button>
      </div>
    </div>
  );
}
