// src/components/Series.js
import React, { useEffect, useState } from 'react';
import './Series.css';

export default function Series() {
  const [leagues, setLeagues] = useState([]);
  const [selectedLeague, setSelectedLeague] = useState(null);
  const [fixtures, setFixtures] = useState([]);
  const [filteredFixtures, setFilteredFixtures] = useState([]);
  const [activeTab, setActiveTab] = useState('Upcoming');
  const [searchTerm, setSearchTerm] = useState('');
  const [loading, setLoading] = useState(false);

  const LIVE_STATUSES = ['LUNCH', 'BREAK', 'LIVE', 'DELAYED', 'INTERRUPTED', 'DINNER'];

  useEffect(() => {
    fetch('/api/leagues')
      .then(res => res.json())
      .then(data => setLeagues(data.data || []));
  }, []);

  useEffect(() => {
    if (!selectedLeague) return;

    const today = new Date().toISOString().slice(0, 10);
    const future = new Date(Date.now() + 90 * 24 * 60 * 60 * 1000).toISOString().slice(0, 10);

    setLoading(true);
    fetch(`/api/fixtures?league_id=${selectedLeague.id}&start=${today}&end=${future}`)
      .then(res => res.json())
      .then(data => {
        setFixtures(data.data || []);
        setLoading(false);
      }).catch(() => setLoading(false));
  }, [selectedLeague]);

  useEffect(() => {
    const term = searchTerm.toLowerCase();
    const filtered = fixtures.filter(fix => {
      const status = fix.status || '';
      const isMatchTab = (
        (activeTab === 'Upcoming' && status === 'NS') ||
        (activeTab === 'Live' && LIVE_STATUSES.includes(status)) ||
        (activeTab === 'Completed' && status !== 'NS' && !LIVE_STATUSES.includes(status))
      );
      return isMatchTab && (
        fix.localteam?.name?.toLowerCase().includes(term) ||
        fix.visitorteam?.name?.toLowerCase().includes(term) ||
        fix.venue?.name?.toLowerCase().includes(term)
      );
    });

    setFilteredFixtures(filtered);
  }, [fixtures, activeTab, searchTerm]);

  const groupByDate = (matches) => {
    return matches.reduce((acc, fix) => {
      const date = new Date(fix.starting_at).toLocaleDateString();
      if (!acc[date]) acc[date] = [];
      acc[date].push(fix);
      return acc;
    }, {});
  };

  const exportFixtures = () => {
    const csv = filteredFixtures.map(f =>
      `${f.starting_at},${f.localteam?.name} vs ${f.visitorteam?.name},${f.venue?.name}`
    ).join('\n');
    const blob = new Blob([csv], { type: 'text/csv' });
    const a = document.createElement('a');
    a.href = URL.createObjectURL(blob);
    a.download = 'fixtures.csv';
    a.click();
  };

  if (!selectedLeague) {
    return (
      <div className="series-container">
        <h2>Series</h2>
        <div className="league-list">
          {leagues.map(l => (
            <div key={l.id} className="league-card" onClick={() => setSelectedLeague(l)}>
              <h4>{l.name}</h4>
              <p>{l.type}</p>
            </div>
          ))}
        </div>
      </div>
    );
  }

  const grouped = groupByDate(filteredFixtures);

  return (
    <div className="series-container">
      <button className="back-btn" onClick={() => setSelectedLeague(null)}>‚Üê Back to Leagues</button>
      <h2>{selectedLeague.name}</h2>

      <div className="series-tabs">
        {['Upcoming', 'Live', 'Completed'].map(tab => (
          <button
            key={tab}
            className={activeTab === tab ? 'active' : ''}
            onClick={() => setActiveTab(tab)}
          >
            {tab}
          </button>
        ))}
      </div>

      <input
        type="text"
        placeholder="Search team or venue..."
        value={searchTerm}
        onChange={e => setSearchTerm(e.target.value)}
        className="search-bar"
      />

      <button onClick={exportFixtures} className="export-btn">Export</button>

      {loading ? <p>Loading fixtures...</p> : (
        Object.keys(grouped).map(date => (
          <div key={date} className="fixture-group">
            <h4>{date}</h4>
            {grouped[date].map(fix => (
              <div key={fix.id} className="fixture-card">
                <strong>{fix.localteam?.name}</strong> vs <strong>{fix.visitorteam?.name}</strong>
                <p>{new Date(fix.starting_at).toLocaleTimeString()}</p>
                <p>{fix.venue?.name}</p>
              </div>
            ))}
          </div>
        ))
      )}
    </div>
  );
}
