import React, { useEffect, useState } from 'react';
import './Teams.css';

const Teams = () => {
  const [teams, setTeams] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchTeams = async () => {
      try {
        const res = await fetch('http://localhost:5050/api/teams');
        const data = await res.json();
        setTeams(data.data || []);
      } catch (err) {
        console.error('Error fetching teams:', err);
      }
    };
    fetchTeams();
  }, []);
console.log(teams);
  return (
    <div className="teams-container">
      <h2 className="teams-heading">All Teams</h2>

      <div className="teams-grid">
        {teams.map((team) => (
          <div key={team.id} className="team-card">
            <img src={team.image_path} alt={team.code} className="team-logo" />
            <span className="team-name">{team.name}</span>
            <h3>{team.name}</h3>
            <p>{team.code}</p>

            {team.country && (
              <div className="country-info">
                <img src={team.country.image_path} alt={team.country.name} className="country-flag" />
                <span>{team.country.name}</span>
              </div>
            )}
          </div>
        ))}
      </div>
    </div>
  );
};

export default Teams;
