import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useNavigate, useParams } from 'react-router-dom';
import CountdownTimer from './../CountdownTimer';
import NotificationButton from './../NotificationButton';
import './css/SeriesMatchesTab.css';

const FORMAT_CODES = ['T20', 'T10', 'ODI', 'T20I', '4DAY', 'TEST', 'TEST/5DAY', 'LIST A'];

export default function SeriesMatchesTab() {
  const { seriesId } = useParams();
  const [matches, setMatches] = useState([]);
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchMatches = async () => {
      setLoading(true);

      try {
        const stageId = parseInt(seriesId);

        // Fetch stage info first to validate if stageId + 1 is valid
        const [stageRes, nextStageRes] = await Promise.allSettled([
          axios.get(`/api/stages/${stageId}`),
          axios.get(`/api/stages/${stageId + 1}`)
        ]);

        const stage = stageRes.status === 'fulfilled' ? stageRes.value.data.data.data : null;
        const nextStage = nextStageRes.status === 'fulfilled' ? nextStageRes.value.data.data.data : null;

        const validNextStage =
          nextStage &&
          stage &&
          stage.season_id === nextStage.season_id;

        // Fetch matches for the confirmed stages
        const stageMatchRes = await axios.get(`/api/series/${stageId}/matches`);
        const matchList = [...(stageMatchRes.data.data?.data || [])];

        if (validNextStage) {
          const nextStageMatchRes = await axios.get(`/api/series/${stageId + 1}/matches`);
          matchList.push(...(nextStageMatchRes.data.data?.data || []));
        }

        setMatches(matchList);
      } catch (err) {
        console.error('Error fetching series matches:', err);
      } finally {
        setLoading(false);
      }
    };

    fetchMatches();
  }, [seriesId]);

  const liveMatchStatuses = ['1st Innings', '2nd Innings', '3rd Innings', '4th Innings', 'Stump Day 1', 'Stump Day 2', 'Stump Day 3', 'Stump Day 4', 'Innings Break', 'Tea Break', 'Lunch', 'Dinner', 'Int.', 'Delayed'];
  const completedMatchStatuses = ['Finished', 'Cancl', 'Aban.'];
  const upcomingMatchStatuses = ['NS', 'Postp.'];

  const getTossWinnerName = (match) => {
    if (match.toss_won_team_id === match.localteam.id) return match.localteam.name;
    if (match.toss_won_team_id === match.visitorteam.id) return match.visitorteam.name;
    return '';
  };

  const getTossDecision = (match) => {
    if (match.elected === 'batting') return 'bat first';
    if (match.elected === 'bowling') return 'bowl first';
    return '';
  };

  const getTeamRuns = (match, teamId) => {
    const innings = (match.runs || []).filter(r => r.team_id === teamId);
    return innings.map(r => `${r.score}/${r.wickets} (${r.overs} ov)`).join(' & ');
  };

  const venueName = (match) => {
    return match.venue?.name && match.venue?.city
      ? `${match.venue.name}, ${match.venue.city}`
      : 'TBD';
  };

console.log(matches);

  return (
    <div className="match-cards-row">
      {loading ? <p>Loading matches...</p> :
        matches.map(match => (
          <div key={match.id} className="match-card" onClick={() => navigate(`/match/${match.id}`, { state: { from: 'Series' } })}>
            <div style={{ marginTop: '10px' }}>
              {match.status !== 'Finished' && <NotificationButton match={match} />}
            </div>

            <h4>{match.type}, {match.round}}</h4>

            <p>
              <img src={match.localteam.image_path} alt="" className="flag-icon" />
              {match.localteam.name} {getTeamRuns(match, match.localteam.id)}
            </p>
            <p>
              <img src={match.visitorteam.image_path} alt="" className="flag-icon" />
              {match.visitorteam.name} {getTeamRuns(match, match.visitorteam.id)}
            </p>

            {match.status === 'NS' && !match?.elected && (
              <CountdownTimer startTime={match.starting_at} />
            )}

            {match?.note ? (
              <p className="status">{match.note}</p>
            ) : (
              match?.toss_won_team_id && match?.elected && (
                <p>{getTossWinnerName(match)} won the toss and elected to {getTossDecision(match)}</p>
              )
            )}

            <div className="match-meta">
              <span className="venue">Venue: {venueName(match)}</span>
            </div>

            {match.id && !upcomingMatchStatuses.includes(match.status) && (
              <div className="notify-btn">
                <button
                  onClick={(e) => {
                    e.stopPropagation();
                    navigate(`/match/${match.id}`);
                  }}
                >
                  Scorecard
                </button>
              </div>
            )}
          </div>
        ))
      }
    </div>
  );
}
