import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import './SeriesSquad.css';

const SeriesSquad = () => {
  const seriesId = window.location.pathname.split('/')[2];
  const [squads, setSquads] = useState([]);
  const [loading, setLoading] = useState(true);
  const [fallbackImg, setFallbackImg] = useState(null);

  useEffect(() => {
    if (!seriesId) return;

    const fetchSquads = async () => {
      try {
        const res = await fetch(`/api/series/${seriesId}/squads`);
        const json = await res.json();
        setSquads(json?.data || []);
      } catch (err) {
        console.error('Error loading series squads', err);
        setSquads([]);
      } finally {
        setLoading(false);
      }
    };

    fetchSquads();
  }, [seriesId]);

  useEffect(() => {
    const fetchFallback = async () => {
      try {
        const res = await fetch('https://raw.githubusercontent.com/ashwint87/CricketAds/master/live_cricket_website_default_image_urls');
        const text = await res.text();
        const match = text.match(/url:\s*['"]([^'"]+)['"]/);
        if (match) setFallbackImg(match[1]);
      } catch (err) {
        console.error('Failed to fetch fallback image:', err);
      }
    };

    fetchFallback();
  }, []);

  return (
    <div className="series-squad">
      <h3>ðŸ§¢ Squads</h3>
      {loading ? (
        <p>Loading squads...</p>
      ) : squads.length === 0 ? (
        <p>No squad data available.</p>
      ) : (
        squads.map((team) => (
          <div key={team.teamName} className="squad-team">
            <div className="team-header">
              <img
                src={
                  team.img && team.img.includes('h.cricapi.com')
                    ? fallbackImg || '' // wait for fallbackImg
                    : team.img
                  }
                alt={team.name}
                width={24}
              /> {team.teamname}
              <h4>{team.teamName}</h4>
            </div>
            <ul>
              {(team.players || []).map((player) => (
                <li key={player.id}>
                  <Link to={`/player/${player.id}`} className="player-link">
                    {player.name}
                  </Link>
                  {player.role ? ` â€“ ${player.role}` : ''}
                </li>
              ))}
            </ul>
          </div>
        ))
      )}
    </div>
  );
};

export default SeriesSquad;
