import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';
import CountdownTimer from './../CountdownTimer';

const TeamLiveMatches = ({ teamName }) => {
  const { id } = useParams();
  const [matches, setMatches] = useState([]);
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  useEffect(() => {
    async function fetchLiveMatches() {
      try {
        const res = await axios.get(`/api/teams/${id}/live-matches`);
        setMatches(res.data.data || []);
      } catch (err) {
        console.error('Error fetching live matches:', err);
      } finally {
        setLoading(false);
      }
    }

    fetchLiveMatches();
  }, [id]);

  const getTeamRuns = (match, teamId) => {
    if (!match.runs || !Array.isArray(match.runs)) return '';
    const innings = match.runs.filter(r => r.team_id === teamId);
    return innings
      .map(r => `${r.score}/${r.wickets} (${r.overs} ov)`)
      .join(' & ');
  };

  const matchYear = (match) => {
    return new Date(match.starting_at).getFullYear();
  };

  const getTossWinnerName = (info) => {
    if (info.toss_won_team_id === info.localteam.id) {
      return info.localteam.name;
    } else if (info.toss_won_team_id === info.visitorteam.id) {
      return info.visitorteam.name;
    } else {
      return '';
    }
  };

  const getTossDecision = (info) => {
    if (info.elected === 'batting') {
      return 'bat first';
    } else if (info.elected === 'bowling') {
      return 'bowl first';
    } else {
      return '';
    }
  };

console.log(matches);
  return (
    <div className="team-matches">
      <h2>Live Matches</h2>

      {loading ? (
        <p>Loading...</p>
      ) : matches.length === 0 ? (
        <p>No live matches available.</p>
      ) : (
        <div className="match-list">
          {matches.map((match) => (
            <div
              key={match.id}
              className="match-card"
              onClick={() => navigate(`/match/${match.id}`)}
            >
              {match.type && (
                <span className="match-type-tag">{match.type}</span>
              )}

              <p>{match.league.name} {matchYear(match)}, {match.round}</p>

              <h4>{match.localteam?.name} vs {match.visitorteam?.name}</h4>

              {getTeamRuns(match, match.localteam.id) && (
                <p>{match.localteam.name}: {getTeamRuns(match, match.localteam.id)}</p>
              )}
              {getTeamRuns(match, match.visitorteam.id) && (
                <p>{match.visitorteam.name}: {getTeamRuns(match, match.visitorteam.id)}</p>
              )}

              <p>Venue: {match.venue?.name}, {match.venue?.city}</p>

              {!match.note ? (
                <p className="status">{match.note}</p>
              ) : (
                match?.toss_won_team_id && match?.elected && (
                  <p>{getTossWinnerName(match)} won the toss and elected to {getTossDecision(match)}</p>
                )
              )}

              <div className="notify-btn">
                {match.id && (
                  <button
                    onClick={(e) => {
                      e.stopPropagation();
                      navigate(`/match/${match.id}`);
                    }}
                  >
                    Scorecard
                  </button>
                )}
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}

export default TeamLiveMatches;
