// TeamMatches.js
import React, { useEffect, useState } from 'react';
import { useParams, useLocation, Link } from 'react-router-dom';
import './css/TeamMatches.css';

export default function TeamMatches() {
  const { id } = useParams();
  const { state } = useLocation();
  const teamName = state?.teamName || 'Team';
  const [matches, setMatches] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchMatches = async () => {
      try {
        const res = await fetch(`/api/teams/${id}/matches`);
        const json = await res.json();
        setMatches(json.data || []);
      } catch (err) {
        console.error('Failed to fetch matches', err);
      } finally {
        setLoading(false);
      }
    };
    fetchMatches();
  }, [id]);

  if (loading) return <p>Loading matches for {teamName}...</p>;
  if (!matches.length) return <p>No upcoming matches for {teamName}.</p>;

  return (
    <div className="team-matches-page">
      <h2>Upcoming Matches for {teamName}</h2>
      <div className="match-list">
        {matches.map(match => (
          <Link key={match.id} to={`/match/${match.id}`} className="match-card">
            <div className="series">{match.league?.name} - {match.round}</div>
            <div className="teams">{match.localteam?.name} vs {match.visitorteam?.name}</div>
            <div className="venue">{match.venue?.name}, {match.venue?.city}</div>
            <div className="date">{new Date(match.starting_at).toLocaleString()}</div>
          </Link>
        ))}
      </div>
    </div>
  );
}
