import React, { useEffect, useState } from 'react';
import {
  liveStatuses,
  completedMatchStatuses,
  upcomingMatchStatuses,
  otherMatchStatuses,
} from './../../constants/matchStatusConstants';
import { useSchedule } from './../../context/ScheduleContext';
import MatchCard from './../MatchCard';

const FeaturedMatches = () => {
  const { matches, loading } = useSchedule();
  const [featuredMatches, setFeaturedMatches] = useState(null);

  useEffect(() => {
    const fetchMatches = async () => {
      try {
        if (loading || !Array.isArray(matches)) return;

        const liveMatches = matches.filter(m => liveStatuses.includes(m.status));
        const upcomingMatches = matches.filter(m => upcomingMatchStatuses.includes(m.status));
        const completedMatches = matches.filter(m => completedMatchStatuses.includes(m.status));

        const featured = [];

        // Add up to 2 live matches first
        featured.push(...liveMatches.slice(0, 2));

        // Track how many more we need to make 3
        let remaining = 3 - featured.length;

        // Add 1 upcoming match if available and needed
        if (remaining > 0 && upcomingMatches.length > 0) {
          featured.push(upcomingMatches[0]);
          remaining--;
        }

        // Add completed match if still needed
        if (remaining > 0 && completedMatches.length > 0) {
          featured.push(completedMatches[0]);
          remaining--;
        }

        // Gather remaining matches in extras
        const extras = [
          ...liveMatches.slice(2),
          ...upcomingMatches.slice(1),
          ...completedMatches.slice(1),
        ];

        // If still not 3 featured matches (in extreme edge cases), fill from extras
        while (featured.length < 3 && extras.length > 0) {
          featured.push(extras.shift());
        }

        setFeaturedMatches(featured);
      } catch (err) {
        console.error('Error fetching featured matches:', err);
      }
    };

    fetchMatches();
  }, [matches, loading]);

  if (!featuredMatches) return <div>Loading featured matches....</div>;
  if (loading) return <p>Loading featured matches...</p>;
  if (!featuredMatches.length) return <p>No matches found.</p>;

  return (
    <>
      <div className="live-matches-header">
        <h3>üèè Top Matches</h3>
        <a href="/schedule">View All</a>
      </div>

      <div className="match-cards-row">
        {featuredMatches.map(match => (
          <MatchCard key={match.id} match={match} />
        ))}
      </div>
    </>
  );
};

export default FeaturedMatches;
