import React from 'react';
import './ScorecardTab.css';

const ScorecardTab = ({ match }) => {
  if (!match || !match.batting || !match.bowling || !match.scoreboards) {
    return <div>No scorecard available.</div>;
  }

  const getInnings = (scoreboard) => scoreboard === 'S1' ? '1st Innings' : '2nd Innings';

  const inningsList = ['S1', 'S2'].filter(inning =>
    match.batting.some(b => b.scoreboard === inning)
  );

  const renderInnings = (inningKey) => {
    const battingData = match.batting.filter(b => b.scoreboard === inningKey);
    const bowlingData = match.bowling.filter(b => b.scoreboard === inningKey);
console.log(battingData);
    const extras = match.scoreboards.find(
      s => s.scoreboard === inningKey && s.type === 'extra'
    );
    const totals = match.scoreboards.find(
      s => s.scoreboard === inningKey && s.type === 'total'
    );

    const fallOfWickets = battingData
      .filter(b => b.fow_score && b.fow_balls)
      .map(b => ({
        name: b.batsman?.fullname || 'N/A',
        score: b.fow_score,
        over: b.fow_balls
      }));

    return (
      <div key={inningKey} className="inning-block">
        <h3>{getInnings(inningKey)}</h3>

        {/* Batting */}
        <table className="scorecard-table">
          <thead>
            <tr>
              <th>Batsman</th>
              <th>R</th>
              <th>B</th>
              <th>4s</th>
              <th>6s</th>
              <th>SR</th>
              <th>Dismissal</th>
            </tr>
          </thead>
          <tbody>
            {battingData.map((batsman, idx) => (
              <tr key={idx}>
                <td>{batsman.batsman?.fullname || 'N/A'}</td>
                <td>{batsman.score}</td>
                <td>{batsman.ball}</td>
                <td>{batsman.four_x}</td>
                <td>{batsman.six_x}</td>
                <td>{batsman.rate}</td>
                <td>{batsman.result || 'Not Out'}</td>
              </tr>
            ))}
            {/* Extras Row */}
            {extras && (
              <tr className="extras-row">
                <td colSpan="7">
                  <strong>Extras:</strong> {` ${extras.wide || 0}w, ${extras.noball_runs || 0}nb, ${extras.bye || 0}b, ${extras.leg_bye || 0}lb`}
                </td>
              </tr>
            )}
            {/* Totals Row */}
            {totals && (
              <tr className="total-row">
                <td colSpan="7">
                  <strong>Total:</strong> {`${totals.total}/${totals.wickets} in ${totals.overs} overs`}
                </td>
              </tr>
            )}
          </tbody>
        </table>

        {/* Fall of Wickets */}
        {fallOfWickets.length > 0 && (
          <div className="fow-section">
            <strong>Fall of Wickets:</strong>{' '}
            {fallOfWickets
              .map(fow => `${fow.name} (${fow.score} at ${fow.over} ov)`)
              .join(', ')}
          </div>
        )}

        {/* Bowling */}
        <h4>Bowling</h4>
        <table className="scorecard-table">
          <thead>
            <tr>
              <th>Bowler</th>
              <th>O</th>
              <th>R</th>
              <th>W</th>
              <th>Eco</th>
              <th>Wd</th>
              <th>Nb</th>
            </tr>
          </thead>
          <tbody>
            {bowlingData.map((bowler, idx) => (
              <tr key={idx}>
                <td>{bowler.player?.fullname || 'N/A'}</td>
                <td>{bowler.overs}</td>
                <td>{bowler.runs}</td>
                <td>{bowler.wickets}</td>
                <td>{bowler.rate}</td>
                <td>{bowler.wide}</td>
                <td>{bowler.noball}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    );
  };

  return (
    <div className="scorecard-tab">
      {inningsList.map(inningKey => renderInnings(inningKey))}
    </div>
  );
};

export default ScorecardTab;
