import React, { useEffect, useState } from 'react';
import axios from 'axios';

export default function PartnershipTab({ matchId }) {
  const [data, setData] = useState(null);
  const [activeTab, setActiveTab] = useState(null);

  useEffect(() => {
    axios.get(`/api/match/${matchId}`).then(res => {
      setData(res.data.data);
    });
  }, [matchId]);

  if (!data) return <div>Loading...</div>;

  const lineup = data.lineup || [];
  const balls = data.balls || [];

  const getPlayerName = (id) => {
    const player = lineup.find(p => p.id === id);
    return player?.fullname || 'Unknown';
  };

  const groupedByInning = {};
  balls.forEach(ball => {
    const inning = ball.scoreboard || 'Inning';
    if (!groupedByInning[inning]) groupedByInning[inning] = [];
    groupedByInning[inning].push(ball);
  });

  const partnershipsPerInning = {};

  Object.entries(groupedByInning).forEach(([inning, deliveries]) => {
    const partnerships = [];
    let current = { batsmen: new Set(), balls: [], runs: 0 };

    deliveries.forEach(ball => {
      const batsmanId = ball.batsman_id;
      const run = ball.score?.runs || 0;

      current.batsmen.add(batsmanId);
      current.balls.push(ball);
      current.runs += run;

      if (ball.wicket) {
        partnerships.push(current);
        current = { batsmen: new Set(), balls: [], runs: 0 };
      }
    });

    if (current.balls.length > 0) partnerships.push(current);

    partnershipsPerInning[inning] = partnerships;
  });

  return (
    <div>
      <h3>Batting Partnerships</h3>
      {Object.entries(partnershipsPerInning).map(([inning, partnerships]) => (
        <div key={inning}>
          <h4>{inning}</h4>
          {partnerships.map((p, idx) => {
            const batsmen = Array.from(p.batsmen).map(getPlayerName).join(' & ');
            return (
              <div key={idx} style={{ marginBottom: '10px' }}>
                <strong>{batsmen}</strong>: {p.runs} runs in {p.balls.length} balls
              </div>
            );
          })}
        </div>
      ))}
    </div>
  );
}
