import React, { useEffect, useState } from 'react';
import './NewsTab.css';

const NewsTab = ({ matchName }) => {
  const [articles, setArticles] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchNews = async () => {
      try {
const toSearchQuery = (str) =>
  str
    .replace(/,/g, '')              // remove commas
    .replace(/[^\w\s]/g, '')        // remove special characters but keep spaces
    .trim()
    .replace(/\s+/g, '+');          // finally replace spaces with +

const searchQuery = toSearchQuery(matchName);
console.log('Search Query:', searchQuery);  // should look like Zimbabwe+vs+South+Africa+2nd+Test

const response = await fetch(`/api/news?q=${searchQuery}`);



        const json = await response.json();
        setArticles(json.articles || []);
      } catch (error) {
        console.error('Error fetching news articles', error);
      } finally {
        setLoading(false);  // ✅ stop loading
      }
    };

    fetchNews();
  }, []);

  if (loading) {
    return (
      <div style={{ padding: '20px' }}>
        <p>Loading News...</p>
      </div>
    );
  }

  if (!articles || articles.length === 0) {
    return (
      <div style={{ padding: '20px' }}>
        <p>No articles at the moment.</p>
      </div>
    );
  }

  return (
    <div className="news-container">
      <div className="news-grid">
        {articles.map((article, index) => (
          <div key={index} className="news-card">
            <img
              src={article.image}
              alt={article.title}
              className="news-image"
            />
            <h2 className="news-heading">{article.title}</h2>
            <p className="news-meta">
              {article.source.name} •{' '}
              {new Date(article.publishedAt).toLocaleString()}
            </p>
            <p className="news-description">{article.description}</p>
            <a
              href={article.url}
              target="_blank"
              rel="noreferrer"
              className="news-link"
            >
              Read more →
            </a>
          </div>
        ))}
      </div>
    </div>
  );
}

export default NewsTab;
