import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './Teams.css';

const Teams = () => {
  const [teams, setTeams] = useState([]);
  const [loading, setLoading] = useState(true);
  const [activeTab, setActiveTab] = useState('international');
  const navigate = useNavigate();

  useEffect(() => {
    const fetchTeams = async () => {
      try {
        const res = await fetch('/api/teams');
        const json = await res.json();
        setTeams(json.data || []);
      } catch (err) {
        console.error('Error loading teams:', err);
      } finally {
        setLoading(false);
      }
    };

    fetchTeams();
  }, []);

  const internationalTeams = teams.filter((t) => t.national_team);
  const domesticTeams = teams.filter((t) => !t.national_team);

  const renderTeamCard = (team) => (
    <div
      key={team.id}
      className="team-card"
      onClick={() =>
        navigate(`/teams/${team.name}/series`, {
          state: { from: 'Teams', teamName: team.name }
        })
      } 
    >
      <img
        src={team.image_path}
        alt={team.name}
        onError={(e) => {
          e.target.src = 'https://em-content.zobj.net/source/apple/354/white-flag_1f3f3-fe0f.png';
        }}
      />
      <p>{team.name}</p>
    </div>
  );

  const renderTabContent = () => {
    const relevantTeams = activeTab === 'international' ? internationalTeams : domesticTeams;
    return (
      <div className="teams-grid">
        {relevantTeams.map(renderTeamCard)}
      </div>
    );
  };

  return (
    <>
    <Breadcrumb items={[
      { label: 'Home', link: '/' },
      { label: 'Teams', link: '/teams' }
    ]} />

    <div className="teams-page">
      <h2>üèè Cricket Teams</h2>

      <div className="tabs">
        <button
          className={activeTab === 'international' ? 'active' : ''}
          onClick={() => setActiveTab('international')}
        >
          üåç International Teams
        </button>
        <button
          className={activeTab === 'domestic' ? 'active' : ''}
          onClick={() => setActiveTab('domestic')}
        >
          üè† Domestic & Franchise Teams
        </button>
      </div>

      {loading ? <p>Loading teams...</p> : renderTabContent()}
    </div>
    </>
  );
};

export default Teams;
