import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import InfoTab from './tabs/InfoTab';
import ScorecardTab from './tabs/ScorecardTab';
import LineupTab from './tabs/LineupTab';
import PartnershipsTab from './tabs/PartnershipsTab'; // Add this import
import './MatchDetails.css';
import { useLocation, useNavigate } from 'react-router-dom';

const MatchDetails = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [matchData, setMatchData] = useState(null);
  const [tab, setTab] = useState('info');
  const location = useLocation();
  const from = location.state?.from || 'home';

  useEffect(() => {
    const fetchMatchDetails = async () => {
      try {
        const response = await fetch(`/api/match/${id}`);
        const json = await response.json();
        setMatchData(json.data);
      } catch (error) {
        console.error('Error fetching match details', error);
      }
    };

    fetchMatchDetails();
  }, [id]);

  if (!matchData) return <div className="loading">Loading match details...</div>;

  const renderTab = () => {
    switch (tab) {
      case 'info':
        return <InfoTab match={matchData} />;
      case 'scorecard':
        return <ScorecardTab match={matchData} />;
      case 'lineup':
        return <LineupTab match={matchData} />;
      case 'partnerships':
        return <PartnershipsTab match={matchData} />;
      default:
        return null;
    }
  };

  return (
    <div className="match-details">
      <button onClick={() => {
        if (from === 'series') {
          navigate('/series');
        } else {
          navigate('/');
        }
      }}>
        {from === 'series' ? '← Back to Series Fixtures' : '← Back to Matches'}
      </button>

      <div className="match-header">
        <div className="team">
          <img
            src={matchData.localteam.image_path}
            alt={matchData.localteam.code}
            className="team-flag"
          />
          <span className="team-name">{matchData.localteam.name}</span>
        </div>

        <span className="vs">vs</span>

        <div className="team">
          <img
            src={matchData.visitorteam.image_path}
            alt={matchData.visitorteam.code}
            className="team-flag"
          />
          <span className="team-name">{matchData.visitorteam.name}</span>
        </div>
      </div>

      <div className="tabs">
        {['info', 'scorecard', 'lineup', 'partnerships'].map((key) => (
          <button
            key={key}
            className={tab === key ? 'active' : ''}
            onClick={() => setTab(key)}
          >
            {key.charAt(0).toUpperCase() + key.slice(1)}
          </button>
        ))}
      </div>

      <div className="tab-content">{renderTab()}</div>
    </div>
  );
};

export default MatchDetails;
